  .ui_actions_menu_item_sublist
  {
    background-position: calc(100% - 15px) center;
  }

  .ui_actions_menu_item_sublist
  {
    border: none;
  }

  .ui_actions_menu_sublist .ui_actions_menu_item.checked
  {
    background: url("data:image/svg+xml;utf8,<svg fill='rgba(171, 175, 179, 1)' height='20' viewBox='0 0 24 24' width='20' xmlns='http://www.w3.org/2000/svg'><path d='M0 0h24v24H0z' fill='none'/><path d='M9 16.17L4.83 12l-1.42 1.41L9 19 21 7l-1.41-1.41z'/></svg>") 10px center no-repeat !important;
  }

  .post_full_like_wrap
  {
    border: none;
  }

  .tt_w.tt_up:after, .tt_w.tt_up:before
  {
    display: none;
  }

  .mention_tt_photo, .mention_tt_img
  {
    width: 50px;
    height: 50px;
  }

  .submit_post
  {
    border-color: rgba(0, 0, 0, .01);
  }

  .tt_w.like_tt.tt_down:after, .tt_w.poll_tt.tt_down:after
  {
    display: none;
  }

  .ui_tabs_search_opened .ui_tab_search_wrap .ui_search
  {
    height: 100%;
  }

  .box_controls
  {
    border: none;
    background: #fff;
    padding: 14px;
    height: 33px;
    border-radius: 0 0 8px 8px;
  }

  .box_controls.wk_voting_selectors
  {
    height: auto;
  }

  .box_x_button, .box_x_button.box_x_tabs
  {
    width: 20px;
    height: 20px;
    background: url("data:image/svg+xml;utf8,<svg fill='rgba(0, 0, 0, .5)' height='20' viewBox='0 0 24 24' width='20' xmlns='http://www.w3.org/2000/svg'><path d='M19 6.41L17.59 5 12 10.59 6.41 5 5 6.41 10.59 12 5 17.59 6.41 19 12 13.41 17.59 19 19 17.59 13.41 12z'/><path d='M0 0h24v24H0z' fill='none'/></svg>") center center no-repeat !important;
    padding-top: 13px;
    transition-duration: .24s;
    z-index: 9;
  }

  #stl_bg
  {
    padding-top: 70px;
  }

  .wide_column .page_upload_label.page_photos_upload
  {
    background: url("data:image/svg+xml;utf8,<svg fill='rgba(0, 0, 0, .35)' height='24' viewBox='0 0 24 24' width='24' xmlns='http://www.w3.org/2000/svg'><circle cx='12' cy='12' r='3.2'/><path d='M9 2L7.17 4H4c-1.1 0-2 .9-2 2v12c0 1.1.9 2 2 2h16c1.1 0 2-.9 2-2V6c0-1.1-.9-2-2-2h-3.17L15 2H9zm3 15c-2.76 0-5-2.24-5-5s2.24-5 5-5 5 2.24 5 5-2.24 5-5 5z'/><path d='M0 0h24v24H0z' fill='none'/></svg>") center center no-repeat !important;
  }

  #audio_status_tt .checkbox
  {
    overflow: visible;
  }

  .media_selector>.ms_item
  {
    margin-right: 10px;
  }

  .media_selector>.ms_item:last-of-type
  {
    margin-right: 0;
    margin-left: 0;
  }

  .im-page.im-page_classic .im-mess .im-mess--fav
  {
    margin-right: 35px;
  }

  #top_notify_btn .top_nav_btn_icon:after
  {
    display: block !important;
  }

  .mail_box_header_link
  {
    color: #999;
  }

  .ui_tabs.ui_tabs_box, .page_block_header, .mail_box_cont
  {
    background: #fff;
  }

  #groups_filters_wrap > div:nth-child(2)
  {
    display: none;
  }

  .photos_choose_upload_area, .web_cam_photo
  {
    color: #999;
    background: #fff;
    transition: all .24s ease-in-out;
  }

  .photos_choose_upload_area:hover, .web_cam_photo:hover
  {
    background: #fafafa;
    color: #444;
  }

  .photo_upload_separator
  {
    display: none;
  }

  .video_block_layout
  {
    padding: 0;
  }

  .ui_tabs.video_default_tabs
  {
    margin-top: 0;
  }

  #video_subtab_pane_all
  {
    padding: 0 20px;
  }

  .audio_play
  {
    background-color: var(--main-color);
  }

  .audio_row.audio_row_current, .audio_row.audio_row_playing, .audio_row.audio_row_current:hover, .audio_row.audio_row_playing:hover, .audio_row:not(.audio_row_current):hover
  {
    background: #fff !important;
  }

  .audio_rows .audio_row .audio_play_wrap
  {
    opacity: 0;
    margin-left: -40px;
    overflow: hidden;
    margin-top: 10px;
  }

  .audio_rows .audio_row .audio_play_wrap .audio_play
  {
    opacity: 0;
    margin-left: -40px;
    transition-property: margin, opacity;
    transition-duration: .24s;
    transition-timing-function: ease-in-out;
    background: none !important;
    border-radius: 0;
    position: relative;
