    width: 100%;
    height: 1px;
    bottom: 0;
    left: 0;
    background-color: #2a5885;
    visibility: hidden;
    -webkit-transform: scaleX(0);
    transform: scaleX(0);
    -webkit-transition: all 0.3s ease-in-out 0s;
    transition: all 0.3s ease-in-out 0s;
  }
  .group_row_labeled > a:hover:before, 
  .friends_field_title > a:hover:before, 
  a.author:hover:before,
  .post_author .author  > a:hover:before, 
  .wall_module .author  > a:hover:before, 
  .wall_module  > a:hover:before,
  a.copy_author:hover:before,
  .topic_title > a:hover:before, 
  .labeled > a:hover:before,
  .people_cell_name > a:hover:before, 
  .right_list_field > a:hover:before, 
  a.friends_field_act:hover:before, 
  a.app_link:hover:before {
    visibility: visible;
    -webkit-transform: scaleX(1);
    transform: scaleX(1);
  }
.profile_info .label {
    font-weight: bold;
  }
  .page_name, h2.page_name {
    font-weight: bold;
    color: #4975a8;
  }
  .is_online, .profile_online_lv, .profile_time_lv {
	text-transform: uppercase;
    font-weight: bold;
    color: #16ca00;
    font-family: "Google Sans";
  }
  .profile_time_lv {
    color: #a8a8a8;
  }
  .current_text {
    font-size: 15px;
  }
  .my_current_info {
  	margin-top: 1px;
    border-radius: 15px;
  }
.profile_info .label {
    padding-right: 100px;
  }
  .profile_more_info_link {
    padding-left: 265px;
  }
}

