style.css:
@-moz-document domain("gofuckingwork.com"), domain("") {
h1,
h2,
h3,
h4,
h5,
h6,
p {
    color: #fff;
}

body {
    background-color: #222;
}

a, a:visited {
    background: linear-gradient(0deg, #fff200 50%, #222 50%) !important;
}

#twitter-share {
    background: linear-gradient(0deg, #6dcff6 50%, #222 50%) !important;
}

#facebook-share {
    background: linear-gradient(0deg, #85c3ff 50%, #222 50%) !important;
}
}
