style.css:
/* ==UserStyle==
@name         An almost complete remake 2.0 of css for the game Pardus with more of everything.
@namespace    https://userstyles.org/styles/167883/pardus-style-remake-2-0
@version      2.0.3
@license      CC BY-SA - Creative Commons Attribution-ShareAlike
==/UserStyle== */

@-moz-document domain("pardus.at") {
/* Configuration */

/* set ship in dock image */
img[title~="dock."] {
    background:url(https://i.pinimg.com/originals/85/5c/75/855c75d1823f30bd65d2d78c4d0338ed.jpg);
    background-position: center top;
}

/* Remove below if you want to exchange certain skills for ASP */
input[value="maneuver"]+input {
    pointer-events: none;
    filter: saturate(0) contrast(.75);
} 
input[value="engineering"]+input {
    pointer-events: none;
    filter: saturate(0) contrast(.75);
} 

/* get teuton normal CE font and install in your system */
/* get teuton normal CE at http://gramajda.hekko24.pl/p/pardus/teutonnormalce.ttf */
/* if the font doesn't work after installed, 'Rationale' is similiar, but a little too narrow fallback font */
@font-face {
  font-family: 'Rationale';
  font-style: normal;
  font-weight: 400;
  src: local('Rationale'), local('Rationale-Regular'), url(https://fonts.gstatic.com/s/rationale/v10/9XUnlJ92n0_JFxHIfHcccVtN.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}

body, table, th, td, div, a, span, input, button, select {
    font-family: 'teuton normal CE', 'Rationale', arial, sans-serif !important;
}
body > table {
    font-size: 1.25em !important;
}
table, span {
    font-size: inherit !important;
}
input, button, select {
    font-size: 1em !important;
}
font[size="1"] {
    font-size: 13px;
}
#ipbwrapper,
#ipbwrapper .postcolor,
#ipbwrapper td {
    font-size: 16px;
}
/* Remove above font rules if you don't have teutonnormalce.ttf installed or don't like Rationale font */

/* manual */
@media (min-width: 1330px) {
  #rand {
    margin: auto;
    transform: scale(1.35);
    transform-origin: 50% 0;
  }
}

/* End of configuration */

::-webkit-scrollbar {
    background: #080F13;
    width: 14px;
}
::-webkit-scrollbar-thumb {
    background: #074065;
    border: 1px solid #080F13;
    border-radius: 2ex;
}

:root {
  --text-color: #A6D1F6;
  --action-color: #8DF4F5;
  --link-color: #CBFFF8;
  --link-rgb: 203, 255, 248;
  --main-blue-color: #2776C3;
  --main-blue-rgb: 39, 118, 195;
  --teal-color: #308178;
  --teal-rgb: 57, 153, 141;
}

html {
    position: relative;
    scrollbar-color: #074065 #080F13;
}
body {
    background:url(http://gramajda.hekko24.pl/p/background.png) #111 !important;
    background-position: -60px -36px !important;
    background-attachment: fixed !important;
    color: var(--text-color) !important;
    margin:0px !important;
}
table {
    color: var(--text-color) !important;
}
div {
    scrollbar-color: var(--main-blue-color) rgba(0,0,0,0.2);
    scrollbar-width: thin;
}
/* page scrollbar flicker fix */
body[style] > table {
    width: calc(100vw - 20px) !important;
    padding-left: 20px;
}

/* huge screen spacing */
td[height="58"][width="30"] > img {
    width: calc(50vw - 840px) !important;
    min-width: 9px;
}
body > table > tbody > tr > td[width="25%"]:first-child {
    padding-left: calc(50vw - 850px) !important;
    border-left: 20px solid transparent;
}
body > table > tbody > tr > td[width="25%"]:last-child {
    padding-right: calc(50vw - 850px) !important;
    border-right: 20px solid transparent;
}
#tdTabsLeft {
    padding-left: calc(50vw - 850px) !important;
}
#tdTabsRight {
    padding-right: calc(50vw - 850px) !important;
}

/* panels */
img[src*="/actions.png"] {
background:url(http://gramajda.hekko24.pl/p/actions.png) !important;
width:0px !important;
height:0px !important;
padding: 23px 110px 23px 110px !important;
}
img[src*="/status.png"] {
background:url(http://gramajda.hekko24.pl/p/status.png) !important;
width:0px !important;
height:0px !important;
padding: 23px 110px 23px 110px !important;
}
img[src*="/cargo.png"] {
background:url(http://gramajda.hekko24.pl/p/cargo.png) !important;
width:0px !important;
height:0px !important;
padding: 23px 110px 23px 110px !important;
}
img[src*="/commands.png"] {
background:url(http://gramajda.hekko24.pl/p/commands.png) !important;
width:0px !important;
height:0px !important;
padding: 23px 110px 23px 110px !important;
}
img[src*="/otherships.png"] {
background:url(http://gramajda.hekko24.pl/p/otherships.png) !important;
width:0px !important;
height:0px !important;
padding: 23px 110px 23px 110px !important;
}
img[src*="/ship.png"] {
background:url(http://gramajda.hekko24.pl/p/ship.png) !important;
width:0px !important;
height:0px !important;
padding: 23px 110px 23px 110px !important;
}
img[src*="/position.png"] {
background:url(http://gramajda.hekko24.pl/p/mapperdata.png) !important;
width:0px !important;
height:0px !important;
padding: 23px 110px 23px 110px !important;
}
td[style*="/panel.png"] {
background:url(http://gramajda.hekko24.pl/p/panel.png) !important;
background-repeat:repeat-y !important;
padding-left:6px !important;
}
img[src*="/panelbottom.png"] {
background:url(http://gramajda.hekko24.pl/p/pbottom.png) !important;
width:0px !important;
height:0px !important;
padding: 8px 110px 8px 100px !important;
margin: 0px 0px 0px 7px !important;
}
td[valign=top] > table[width="210"] {
margin:-11px 6px -15px 8px;
}
#status_content {
    margin: auto !important;
    max-width: 180px;
}
#status_content > table {
    width: 100%;
}
#tdStatusSector > * {
    display: inline-block;
    margin-right: -20px;
}
#tdStatusCoords > * {
    display: inline-block;
    padding-left: 20px;
    margin-right: -20px;
}
#credits {
    display: inline-block;
    margin-left: -8px;
}
#commands_content a {
    line-height: 20px;
}
#commands_content #aCmdPlanet:after,
#commands_content #aCmdStarbase:after,
#commands_content #aCmdBuilding:after,
#commands_content a[id*="Trade"]:after,
#commands_content a#aCmdOwnBuilding:after,
#commands_content #aCmdShipEquipment:after,
#commands_content a[href="bulletin_board.php"]:after {
    content: '';
    position: absolute;
    left: 194px;
    margin-top: -44px;
    padding: 14px;
    border: 1px solid var(--teal-color);
    box-shadow: 0 0 0px 3px #000, 0 0 0px 4px #002A64;
    border-radius: 50%;
    background-size: 18px;
    background-repeat: no-repeat;
    background-position: center;
    background-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="10 0 620 512"><path d="M624 448H16c-9 0-16 7-16 16v32c0 9 7 16 16 16h608c9 0 16-7 16-16v-32c0-9-7-16-16-16zM45 206l89 80a63 63 0 0025 14l288 78c26 7 54 9 81 1 29-8 43-21 47-35 4-15-2-33-23-55-20-20-45-33-71-40l-98-27L283 30c-2-6-6-10-12-12L206 1c-11-3-21 5-21 16l48 164-102-28-28-67c-1-5-6-9-11-10L53 65c-11-3-21 5-21 16v101c0 9 6 18 13 24z" fill="%23DFF" /></svg>');
}
#commands_content a#aCmdOwnBuilding:after,
#commands_content a[id*="Trade"]:after {
    margin-top: -10px;
    background-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="20 0 462 512"><path d="M202 335c0 16 13 29 29 29h72c13 0 19-4 29-10 6-5 14-4 19 2l35 38c7 6 6 18-2 24-25 19-49 30-80 30v31c0 19-8 18-16 20l-44 9c-9 1-16 5-16-12v-48c-47-1-106-29-106-101V184c0-71 53-120 105-120V33c0-9 7-18 16-20l44-9c10-2 16 3 16 12v48c32 0 56 11 81 30 8 6 9 18 1 25l-34 38c-6 5-14 6-20 2-10-7-22-11-34-11h-66c-17 0-30 13-30 30" fill="%23DFF" /></svg>');
}
#commands_content #aCmdShipEquipment:after {
    margin-top: 24px;
    background-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="6 6 500 500"><path d="M255 8c-18 0-36 2-54 6-5 1-9 6-9 12v49c-22 8-43 20-61 35L89 85c-5-2-11-1-15 3-25 27-44 59-55 94-2 6 1 12 6 14l42 25c-4 23-4 47 0 70l-42 25c-5 3-7 8-6 14 11 35 30 68 55 94 4 5 10 6 15 3l42-25c18 16 39 27 61 35v49c0 6 4 11 9 12 37 8 75 8 110 0 5-1 9-6 9-12v-18l-65-114c-41 3-73-8-95-46-20-35-17-67 1-100 3-7 13-7 16 0l40 68c9 16 84-29 75-44l-39-68c-4-6 1-14 8-14 38 1 67 14 88 50 21 38 15 71-8 105l64 111 18 11c5 2 11 2 15-3 25-26 44-59 55-94 1-6-1-12-6-14l-42-25c4-23 4-47 0-70l42-25c5-3 7-8 6-14-11-35-30-68-55-94-4-4-10-5-15-3l-42 25c-18-15-39-27-61-35V26c0-6-4-11-9-12-19-4-37-6-56-6z" fill="%23DFF" /></svg>');
}
#commands_content a[href="bulletin_board.php"]:after {
    margin-top: 38px;
    background-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="12 0 484 512"><path d="M256 32C115 32 0 125 0 240c0 50 21 95 57 131-13 50-54 95-55 96a8 8 0 006 13c66 0 116-32 141-51 32 12 69 19 107 19 141 0 256-93 256-208S397 32 256 32zm24 302v18c0 9-7 16-16 16h-16c-9 0-16-7-16-16v-18c-11-1-22-5-32-11-6-4-7-13-1-19l17-17c4-4 9-4 14-2l11 2h32a8 8 0 003-16l-50-15c-23-6-40-24-43-47-4-32 19-60 49-63v-18c0-9 7-16 16-16h16c9 0 16 7 16 16v18c11 1 22 5 32 11 6 4 7 13 1 19l-17 17c-4 4-9 4-14 2a25 25 0 00-11-2h-32a8 8 0 00-3 16l50 15c23 6 40 24 43 47 4 32-19 60-49 63z" fill="%23DFF" /></svg>');
}
#commands_content a:hover:after {
    filter: brightness(1.3) !important;
}
#aCmdCollect2,
#aCmdCollect5 {
    position: relative;
    z-index: 1;
    left: 10px;
    margin-left: -10px;
    margin-right: -30px;
    color: black !important;
    font-weight: bold;
    font-size: 12px;
}
#aCmdCollect5 {
    left: 55px;
}
#aCmdCollect2:before,
#aCmdCollect5:before {
    content: '';
    box-sizing: content-box;
    position: absolute;
    top: -5px;
    bottom: -4px;
    left: -2px;
    right: -5px;
    background: var(--action-color);
    box-shadow: 0 0 5px rgba(0,0,0,1);
    border-radius: 4px 2.5px;
    transform: skew(-9deg);
    opacity: .8;
    transition: all .2s;
    z-index: -1;
}
#aCmdCollect2:hover:before,
#aCmdCollect5:hover:before {
    opacity: 1;
}
#tableCargoRes {
    width: 140px;
    margin: auto;
    white-space: nowrap;
}
#cargo_content .hlLink:after {
    content: '';
    position: absolute;
    top: -6px;
    left: -5px;
    padding: 23px;
    border-radius: 50%;
    border: 1px solid var(--action-color);
    border-top-color: transparent;
    border-left-color: transparent;
    transform: rotate(45deg);
}
#cargo_content .hlLink {
    position: relative;
    display: inline-block;
    margin: 1px 35px -50px;
    width: 28px;
    height: 31px;
    padding: 7px 5px 0;
    border-radius: 50%;
    border: 1px solid var(--teal-color);
    border-bottom-width: 2px;
    border-top-color: transparent;
    background: repeating-linear-gradient(180deg,
        transparent 0 5px,
        rgba(var(--teal-rgb), .3) 5px 5px,
        rgba(0, 0, 0, 0) 15px 100%);
    line-height: 12px;
    font-weight: normal;
    text-align: center;
    text-transform: lowercase;
}
#cargo_content div[style][align] {
    margin: 0 !important;
}
#cargo_content div[style][align] > b {
    display: block;
    margin-top: -30px;
    margin-bottom: 30px;
    font-weight: normal;
}
#cargo a[href="index_buildings.php"] {
    line-height: 0;
}
#cargo a > font[size="1"] {
    position: relative;
    bottom: -18px;
    padding: 6px 10px 11px;
    clip-path: polygon(0 8px, 8px 0, calc(100% - 8px) 0, 100% 8px, calc(100% - 5px) 8px, calc(100% - 5px) 80%, 5px 80%, 5px 8px );
    box-shadow: inset 0 -1px 3.5px 1px var(--action-color);
    border-radius: 11px 11px 0 0;
    color: var(--action-color);
    text-shadow: -1px 1px 0px black, 1px 2px 1px black;
    background: black;
    background-image: 
    repeating-linear-gradient(180deg,
        black 0 1px,
        rgba(0, 0, 0, .6) 18px 23.1px,
        rgba(0, 0, 0, .1) 23px 80px),
    repeating-linear-gradient(60deg, 
        transparent -5px 0px, 
        var(--action-color) 0px 6px);
}
#cargo {
    margin-bottom: 0px;
}
#otherships_content > table[id^='tableScannerShip'] {
    margin: -8px 0 -0px;
}
#otherships_content > table[id] td:first-child {
    padding-top:14px;
    box-sizing: border-box;
}
#otherships[style="display: block;"] {
    display: table !important;
}
#otherships:not([style="display:none;"]) + #yourposition {
    top: 21px;
}
#yourposition {
    position: relative !important;
    animation: emerge .1s linear;
}
#yourposition #mini_map_settings {
    right: 10px;
    top: 10px;
    border-radius: 50%;
    z-index: 1;
    box-sizing: border-box;
    opacity: .75;
    filter: sepia() hue-rotate(130deg);
}
#yourposition:after {
    content: '';
    position: absolute;
    right: 5px;
    top: 5px;
    padding: 10px;
    border: 2px solid var(--teal-color);
    border-right-color: transparent;
    border-left-color: transparent;
    box-shadow: 0 0 0px 1px #000, 0 0 0px 2px #002A64;
    border-radius: 50%;
    transform: rotate(-50deg);
}
#yourposition td[style][align="center"] {
    padding: 0 11px 0 19px !important;
}
#yourposition #miniMapper {
    width: auto !important;
    margin-bottom: -2px;
    margin-top: -2px;
}
#yourposition #miniMapperMap {
    /*left: 50% !important;
    transform: translate(-50%, 0);*/
}
#mini_map_coords:not(:empty) {
    top: auto !important;
    bottom: -7px;
    left: 42%;
    background: #111;
    border: 1px solid var(--teal-color);
    padding: 3px 5px 2px;
    border-radius: 1em;
    font-size: 13px !important;
}

/* nav page & fields */
#tdSpaceChart:before {
    content: '';
    position: absolute;
    top: 0;
    right: 0;
    left: 0;
    bottom: 0;
    min-height: 100vh;
    z-index: -1;
    background: url(http://gramajda.hekko24.pl/p/pardus/spacebg.jpg) top center;
	opacity: .7;
}
#tdSpaceChart > table[align] {
    background: rgba(9, 21, 40, .6);
    border: 1px solid rgba(57, 153, 141, .7) ;
    border-radius: 16px;
    padding: 0px 14px 5px 0;
    margin-top: -6px;
}
#tdSpaceChart > table > tbody > tr > td[style] > div[style] {
    overflow: visible !important;
}
#tdSpaceChart table[style] tr:nth-child(2n) td[class]:first-child {
	box-shadow: -1px 0 0 rgba(0,0,0, .9),
                -3px 0 0 rgba(var(--teal-rgb), .7);
}
#tdSpaceChart table[style] tr:nth-child(2n) td[class]:last-child {
	box-shadow: 1px 0 0 rgba(0,0,0, .9),
                3px 0 0 rgba(var(--teal-rgb), .7);
}
#tdSpaceChart table[style] tr:first-child td[class]:nth-child(2n) {
	box-shadow: 0 -1px 0 rgba(0,0,0, .9),
                0 -3px 0 rgba(var(--teal-rgb), .7);
}
#tdSpaceChart table[style] tr:last-child td[class]:nth-child(2n) {
	box-shadow: 0 1px 0 rgba(0,0,0, .9),
                0 3px 0 rgba(var(--teal-rgb), .7);
}
table#navarea,
table#navareatransition {
border-spacing: 1px;
background:#111!important;
}
table#navarea td,
table#navareatransition td {
background-repeat:no-repeat !important;
background-position:center center !important;
}
table#navarea a,
table#navareatransition a {
    display: block;
	position: relative;
	overflow: hidden;
    cursor: pointer;
}
table#navarea a:hover:before,
table#navareatransition a:hover:before {
    content: '';
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background:url(http://gramajda.hekko24.pl/p/navcoordbg1.png) -1px 0 no-repeat !important;
}
table#navareatransition .navClear a:hover:before {
    z-index: 1;
}
#tdSpaceChart td[style] > a > img {
	position: relative;
}
table#navarea a:active:after,
table#navareatransition a:active:after {
    content: '';
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
	border-radius: 50%;
	background: var(--main-blue-color);
	opacity: 0;
	animation: pulse .2s linear;
}
@keyframes pulse {
  0% {
    opacity: .8;
    transform:scale(0);
  }
  100% {
    opacity: 0;
    transform:scale(2);
  }
}
/* tile highlighter */
#tdSpaceChart tr:nth-child(4):nth-last-child(4) td:nth-child(4):nth-last-child(4) > div[style],
#tdSpaceChart tr:nth-child(4):nth-last-child(4) td:nth-child(5):nth-last-child(5) > div[style],
#tdSpaceChart tr:nth-child(5):nth-last-child(5) td:nth-child(5):nth-last-child(5) > div[style],
#tdSpaceChart tr:nth-child(5):nth-last-child(5) td:nth-child(6):nth-last-child(6) > div[style] {
    background: transparent !important;
}
#tdSpaceChart td[id] {
    overflow: hidden;
}
#tdSpaceChart td[id] > div[style]:empty {
    display: none;
}
#tdSpaceChart td[id] > div[style] {
    margin: -9px;
    padding: 9px;
    border-radius: 10em;
}
#tdSpaceChart tr:nth-child(4):nth-last-child(4) td:nth-child(4):nth-last-child(4) a,
#tdSpaceChart tr:nth-child(4):nth-last-child(4) td:nth-child(5):nth-last-child(5) a,
#tdSpaceChart tr:nth-child(5):nth-last-child(5) td:nth-child(5):nth-last-child(5) a,
#tdSpaceChart tr:nth-child(5):nth-last-child(5) td:nth-child(6):nth-last-child(6) a {
    position: relative;
    z-index:1;
}
#tdSpaceChart tr:nth-child(4):nth-last-child(4) td:nth-child(4):nth-last-child(4) a:before,
#tdSpaceChart tr:nth-child(4):nth-last-child(4) td:nth-child(4):nth-last-child(4) a:after,
#tdSpaceChart tr:nth-child(4):nth-last-child(4) td:nth-child(5):nth-last-child(5) a:before,
#tdSpaceChart tr:nth-child(4):nth-last-child(4) td:nth-child(5):nth-last-child(5) a:after,
#tdSpaceChart tr:nth-child(5):nth-last-child(5) td:nth-child(5):nth-last-child(5) a:before,
#tdSpaceChart tr:nth-child(5):nth-last-child(5) td:nth-child(5):nth-last-child(5) a:after,
#tdSpaceChart tr:nth-child(5):nth-last-child(5) td:nth-child(6):nth-last-child(6) a:before,
#tdSpaceChart tr:nth-child(5):nth-last-child(5) td:nth-child(6):nth-last-child(6) a:after {
    content: '';
    position: absolute;
    border: 10px solid green; 
    top: 4px;
    left: 4px;
    bottom: 4px;
    right: 4px;
    z-index: -1;
    border-radius: 50%;
    border-color: rgba(0, 255, 0,.2) transparent;
    animation: spin 15s linear infinite;
    animation-iteration-count: 3;
}
#tdSpaceChart tr:nth-child(4):nth-last-child(4) td:nth-child(4):nth-last-child(4) a:after,
#tdSpaceChart tr:nth-child(4):nth-last-child(4) td:nth-child(5):nth-last-child(5) a:after,
#tdSpaceChart tr:nth-child(5):nth-last-child(5) td:nth-child(5):nth-last-child(5) a:after,
#tdSpaceChart tr:nth-child(5):nth-last-child(5) td:nth-child(6):nth-last-child(6) a:after {
    top: -9px;
    left: -9px;
    bottom: -9px;
    right: -9px;
    border: 17px ridge rgba(40, 113, 194,.4);
    animation: spin 9s linear infinite;
    animation-iteration-count: 3;
    animation-direction: reverse;
}
    @keyframes spin { 
        from { 
            transform: rotate(0deg); 
        } to { 
            transform: rotate(360deg); 
        }
    }

/* top menu frame */
table[style="height:58px;"] td {
    background-image:none !important;
}
table[style="height:58px;"] td td:not(.menubutton) {
    width: 6px !important;
    max-width: 6px;
    min-width: 6px;
}
.menubutton {
    width: auto !important;
}
td.menubutton > a {
