.coordsh1:last-child,
.coordsh2:last-child {
    margin-right: 1px;
}
.verticalLeft,
.verticalRight {
    position: static;
}
.coord {
    position: relative;
    left: 0;

    font-size:14px;
    color:#199AB1;
    font-weight:bold;
    text-shadow: 0 0px 2px rgba(0,0,0,.9);
    transition: all 1s;
    overflow: hidden;
    animation: emerge .1s linear;
}
.coord:before {
    content: '';
    position: absolute;
    margin-top: -30px;
    margin-left: -30px;
    width: 60px;
    height: 60px;
    background: rgba(141, 189, 255, .24);
    transform: rotate(45deg);
    z-index: -1;
}
.coord:nth-last-child(2n+1):before {
    background: rgba(85, 139, 227, .24);
}
.coordsh1:before {
    left: 50%;
    top: 16px;
}
.coordsh2:before {
    left: 50%;
    bottom: -13px;
}
.coordsv1:before {
    top: 50%;
    left: 16px;
}
.coordsv2:before {
    top: 50%;
    right: -13px;
}
.coordsh1 {
    top: 0;
    display: inline-block;
    width:64px !important;
    height:0 !important;
    line-height: 0 !important;
    margin: 0 0 0 1px;
    padding:14px 0px 14px 0px !important;
    text-align:center;
