}
.coordsh2 {
display: inline-block;
width:64px !important;
bottom: 0;
margin-top: 0;
margin-left: 1px;
padding:15px 0px 13px 0px !important;
line-height:0px !important;
vertical-align: top;
text-align:center;
}
.coordsh2:nth-last-child(2n+1) {
padding-top:12px !important;
}
.coordsv1 {
