/* Clock */
#pardusNavClock {
    position: absolute;
    left:50% ;
    width: 400px;
    margin-left: -200px;
    bottom: 0px;
    line-height: 11px;
    color: inherit !important;
    z-index: 1;
}
#pardusNavClock td { 
    background: rgba(0,0,0,.5) !important;
    border-radius: 1em;
}
#pardusNavClock thead td { 
    font-size: 9px;
    opacity: .3;
    transition: opacity .3s;
}
#pardusNavClock tbody span { 
    font-size: 13px !important;
    opacity: .4;
    transition: opacity .3s;
}
#pardusNavClock thead td[style*="yellow"],
#pardusNavClock tbody span[style*="yellow"] {
    opacity: .5;
}
#pardusNavClock thead td[style*="lime"],
#pardusNavClock tbody span[style*="lime"],
#pardusNavClock thead td[style*="red"],
#pardusNavClock tbody span[style*="red"],
#pardusNavClock:hover thead td,
#pardusNavClock:hover tbody span {
    opacity: 1;
}


/* Advanced Combat Buttons */
@keyframes emerge {
  0% {
    opacity: 0;
  }
  100% {
    opacity: 1;
  }
}
#advanced_combat_buttons_wrapper {
	animation: emerge .1s linear;
    z-index: 1;
    filter: drop-shadow(0px 0px 2px #000) drop-shadow(0px 0px 0px #000);
}
#advanced_combat_buttons_wrapper button {
    position: relative;
    margin: 0px -3px 2px;
    outline: 0 !important;
    border-radius: 50%;
    border: 0px solid transparent;
    background: rgba(255, 0, 0, .038) !important;
    color: #000 !important;
    font-weight: bold !important;
    font-size: 14px !important;
    box-shadow: none !important;
    text-shadow: 0 0px 2px rgba(255,255,255,.2) !important;
    font-family: "trebuchet MS", sans-serif !important;
}
#advanced_combat_buttons_wrapper .enabled {
    cursor: default;
    pointer-events: none;
}
#advanced_combat_buttons_wrapper #btn_adv_combat_AB:before {
    transform: scale(0.88);
    filter: saturate(0) brightness(1.6);
}
#advanced_combat_buttons_wrapper #btn_adv_combat_BA {
    top: -22px;
}
#advanced_combat_buttons_wrapper #btn_adv_combat_BA:before {
    transform: rotate(180deg);
    margin-bottom: -2px;
    margin-top: 2px;
}
#advanced_combat_buttons_wrapper button:not(.enabled):hover:before {
    filter: contrast(1.5) brightness(1.3);   
}
#advanced_combat_buttons_wrapper button:before {
    filter: contrast(1);
    transition: all .2s;
    content: '';
    position: absolute;
    z-index: -1;
    top: -5px;
    bottom: -2px;
    left: -2px;
    right: -2px;
    transform: rotate(0deg);
    background-size: cover;
    background-position: center center;
    background-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="1 1 14 13"><path d="M6.64 2c-.71 0-1.37.38-1.72 1L2.27 7.6a1.99 1.99 0 000 1.98L3.67 12c.35.62 1 1 1.71 1h5.24c.7 0 1.37-.38 1.72-1l1.4-2.42c.35-.61.35-1.37 0-1.99L11.08 3c-.35-.62-1-1-1.72-1z" fill="rgb(39, 118, 195)" /></svg>');
}
#advanced_combat_buttons_wrapper #btn_adv_combat_DC.enabled:before {
    background-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="1 1 14 13"><path d="M6.64 2c-.71 0-1.37.38-1.72 1L2.27 7.6a1.99 1.99 0 000 1.98L3.67 12c.35.62 1 1 1.71 1h5.24c.7 0 1.37-.38 1.72-1l1.4-2.42c.35-.61.35-1.37 0-1.99L11.08 3c-.35-.62-1-1-1.72-1z" fill="rgb(68, 144, 68)" /></svg>');
}
#advanced_combat_buttons_wrapper #btn_adv_combat_BA.enabled:before {
    background-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="1 1 14 13"><path d="M6.64 2c-.71 0-1.37.38-1.72 1L2.27 7.6a1.99 1.99 0 000 1.98L3.67 12c.35.62 1 1 1.71 1h5.24c.7 0 1.37-.38 1.72-1l1.4-2.42c.35-.61.35-1.37 0-1.99L11.08 3c-.35-.62-1-1-1.72-1z" fill="rgb(171, 223, 216)" /></svg>');
