/* Quick Utilities */
#status_content.is-relative {
    position: relative;
    z-index:1;
    text-shadow: 0 0 2px black;
}
.mark-warning {
    position: relative;
}
.mark-critical {
    color: red;
    text-shadow: 1px 0 2px black, -1px 0 2px black;
    transition: color .2s;
}
.mark-warning:after {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    margin: -20px 0 0 -19px;
    padding: 20px;
	border-radius: 50%;
	background: rgba(255, 0, 0, .4);
	animation: emerging .5s ease-in-out;
    z-index: -1;
}
.mark-critical:after {
    margin: -30px 0 0 -30px;
    padding: 30px;
	background: rgba(255, 0, 0, .6);
}
@keyframes emerging {
  0% {
    opacity: 0;
    transform:scale(0);
  }
  70% {
    opacity: 1;
    transform:scale(1.6);
  }
  100% {
    opacity: 1;
    transform:scale(1);
  }
}

