style.css:
@-moz-document domain('tecnoblog.net'){
  @-moz-document domain("disqus.com"){
/*DISQUS DARK BY LILLEN*/
/* COLORS: #18181A BG | #242426 BOX | #BABABB TXT | #EFEFEF LINK */
div{background:transparent!important}
blockquote,.nav--aside,.content-area,.header-area,.card-wrap,.module--aside,.comments-module,ul.nav--aside,.card__additional,.nav--aside,.tooltip,body:not(.home-drawer) .card-notif,body:not(.home-drawer) .card-profile,body:not(.home-drawer) .card-profile-forum,.card,.nav__item,.aside__wrap{color:#BABABB!important;background:#242426!important;border:#18181A 1px solid!important;box-shadow:none!important}
a{color:#EFEFEF!important}
.comment__header .author a,.name{color:#2E9FFF!important}
hr,.aside__header.-border-bottom{border:#18181A 1px solid!important;background:none!important;color:#BABABB!important}
.button-outline,.nav-lnk,.post-actions,.curtain-truncate,.nav-lnk.-color-muted{color:#EFEFEF!important;background:#26262A!important;border:#18181A 1px solid!important}
.button--tag{border:#18181A 1px solid!important;color:#5CB767!important;background-color:#214124!important}
.carousel-control{background:rgba(255,255,255,0.35)!important}
.btn.post-action__button{color:#EFEFEF!important;background:#3F3F41!important}
.label--count,.button--recommend{color:#BABABB!important;background:#18181A!important;border-color:#000!important}
.reading-block,p,abbr,acronym,address,caption,cite,code,dd,del,dfn,dl,dt,em,fieldset,form,h1,h2,h3,h4,h5,h6,img,label,legend,li,ol,p,pre,q,s,samp,small,strike,strong,sub,sup,table,tbody,td,tfoot,th,thead,tr,tt,ul,var{background:none!important;color:#BABABB!important}
.textarea,input,.textarea-wrapper,.placeholder{color:#BABABB!important;background:#000!important}
a.attach{background-color:#BABABB!important}
.bg--light,.bg--white,.site-nav--marketing,.panel.-trusted,.panel{color:#BABABB!important;background:#18181A!important;border:none!important}
.alert--success-light{background-color:#214124!important}
}
body, #content, #content .container, .team, .post-big article .thumb, #post header, #more, #more2 {
    background-color: #212529
}
.adfullblock, #fullbanner, .adfullblock.adfullblock-mobile, .adfullblock #content .container {
    background-color: #495057 !important
}
.adfullblock {
    border-top: none
}
#post h1 a, h3.capa, .team-title, article h2 a, .team-name, .team-name a, .team-list li.team-more a:hover, .entry p, .entry ul, .entry ol, #post h1, #post h2, #post h3, #post h4, #post h5, #post h6 {
    color: #f1f3f5
}
#populares, .tags, #more .grid8 {
    border-top-color: #adb5bd
}
#populares ul li:nth-child(odd), .latest-tutorials ul li:nth-child(odd) a {
    background-color: #ced4da
}
#populares ul li:nth-child(even), .latest-tutorials ul li:nth-child(even) a {
    background-color: #adb5bd
}
#populares li:hover {
    background-color: #f1f3f5
}
.grid4 article.bloco:after {
    background: none
}
.prevnext a {
    background-color:#212529;
    border: 1px solid #ebeced;
}
.prevnext a:nth-child(1) {
    width: 306px;
}
#sobre {
    background: #495057
}
#sobre h1, .tags {
    color: #f1f3f5
}
.wp-caption p.wp-caption-text {
    background-color: #495057 !important;
  color: #f1f3f5
}
}
