@-moz-document domain("stackexchange.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

#tabs a,

.privileges-page .short-description,
.tabs a,
.fc-light,
.topbar-dialog .header h3
{
	text-transform: capitalize!important;
}

#tabs a.youarehere,
#tabs a:hover,

.ba,
.company-tag,
.container .chosen-choices .search-choice,
.container .chosen-container-multi .chosen-choices li.search-choice,
.disliked-tag,
.favorite-tag,
.geo-tag,
.moderator-tag,
.module,
.page-numbers,
.post-tag,
.required-tag,
.s-btn__muted.s-btn__outlined,
.s-sidebarwidget,
.status.answered,
.tabs a.youarehere,
.tabs a:hover,

:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

path:first-child {
    fill: rgb(255, 3, 3);
}

/* Fonts 
_____________________________________________________________________________________________________*/

.mb24,
.question h2,
.s-post-summary--content .s-post-summary--content-title,
.s-prose code,
.s-prose pre:not(.s-code-block)
{
	font-size: 2.5rem!important;
}

.heading,
.module h3,
.module.community-bulletin .bulletin-title,
.pln,
.post-tag,
.post-text code,
.post-text p,
.s-post-summary--content .s-post-summary--content-excerpt,
.page-description,
.pun,
.question-hyperlink,
.subheader h1,
.summary h3,
.topbar-dialog.network-logo-dialog h4,
.users-page .user-info .user-details>a,

p
{
	font-size: 1.8rem!important;
}

.badge,
.badge-tag,
.badges-page .badge-row,
.badges-page .mb,
.comment-copy,
.excerpt,
.faq-page .col-section,
.featured-site .site-details a,
.hero-box em,
.post-text blockquote p,
.privileges-page #privilege-table,
.s-prose,
.topbar-dialog .header h3,
.wmd-preview code
{
	font-size: 1.52rem!important;
}

#hot-network-questions a,
#tags_list .excerpt,

.comment-text,
.featured-site .site-details,
.help-center .help-category-tree li a,
.item-multiplier-count,
.s-sidebarwidget--content,
.topbar-dialog .modal-content,
.users-sidebar .userDetails .sb-userName
{
	font-size: 1.2rem!important;
}

.question h2
{
	line-height: 40px!important;
}

#tags_list .excerpt,

.help-center .help-category-tree li a,
.page-description
{
	line-height: 24px!important;
}

.metaInfo,
.users-page .user-info .user-details .user-location,
.users-page .user-info .user-tags,
.users-sidebar .userDetails .sb-siteName
{
	line-height: 19px!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

#tabs a.youarehere:before,

.js-zone-container.zone-container-main.mb8,
.everyonelovesstackoverflow.everyoneloves__top-sidebar,
.message.message-info .message-tip-left-top:before,
.tabs a.youarehere:before
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Left/Right/Top/Bottom 
_____________________________________________________________________________________________________*/

.flush-left {
    margin-left: -11px!important;
}

/* Margins 
_____________________________________________________________________________________________________*/

#answers,
#mainArea,
#mainbar,
#sideBar,
#sidebar,
#tags_list .tag-cell,

.grid-layout>.grid-layout--cell,
.left-sidebar,
.mainbar,
.question,
.sidebar
{
	margin: 1.2rem!important;
}

.users-sidebar .userDetails {
	margin: 1rem .5rem!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

#answers,
#mainArea,
#mainbar,
#sideBar,
#sidebar,
#tags_list .tag-cell,

.grid-layout>.grid-layout--cell,
.left-sidebar,
.mainbar,
.message.message-info,
.module,
.question,
.sidebar,
.wmx4
{
	padding: 1.2rem!important;
}

p code,
span code
{
	padding: 1px 26px!important;
}


/* Heights 
_____________________________________________________________________________________________________*/

selector
{
	max-height: none!important;
}

#tags_list .excerpt {
	height: auto!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

#content,

.wmx12,
.wmx3,

body>.container
{
	max-width: none!important;
}

selector {
	width: auto!important;
}


.users-sidebar,
.featured-site .site-details {
    width: 93%!important;
}

#content,

.contentWrapper,
.question-container
{
	width: 98%!important;
}

.question.question-hot
{
    width: 92%!important;
}

#mainArea,
#mainbar,

.mainbar
{
    width: 74%!important;
}

.badges-page .badge-description
{
    width: 73%!important;
}

.privileges-page .short-description,
.wmx3
{
    width: 25%!important;
}

#sideBar,
#sidebar,

.sidebar
{
    width: 20%!important;
}

.badges-page .badge-cell-large
{
    width: 17%!important;
}

.badges-page .badge-count,
.left-sidebar
{
    width: 9%!important;
}

