style.css:
@-moz-document domain("mail.ru") {
    html {-webkit-filter:invert(100%);background-color:#000;}
    video,
    iframe,
    .twitter-tweet,
    div[style^="background-image"],
    span[style^="background-image"],
    span[style^="background: url"],
    a[style^="background-image"],
    img {-webkit-filter:invert(100%) !important; opacity:0.8;}
    .news-widget__counter {color: #000;}
    .tabs__item.is-active::after {border-bottom:2px solid #3990ff; color:#3990ff;}
    .feed-generic__heading a,
    .mailbox__icon_back,
    .mailbox__icon_calendar,
    .mailbox__icon_cloud, a {color:#3990ff;}
    .w-pm-menu,
    .pm-menu,
    .logo__img,
    .pm-toolbar__search__container {background-color:#3990ff;}
    .pm-toolbar__button__inner_current {background-color:#1870ff;}
    .b-sticky {position:relative !important;}
    .b-atop {position:relative; bottom:-44px;}
    .search__btn {line-height:38px;}
    .btn_orange {border:1px solid #3990ff; background:#3990ff;}
    .btn_orange:hover {background-color:#3990ff; opacity:0.8;}
    .btn_orange:focus {border-color:#1870ff; background-color:#3990ff;}
    .btn_orange:active {border-color:#1870ff; background-color:#1870ff;}
    .comment-form_state_fixed,
    .p-fixed-panel_fixed {position:static;}
    .screen_medium .article__item {padding-left:0;}
    .article__container {margin:0 auto; width:640px; display:block;}
    .cols__column_news240x400-side,
    .screen_medium .cols__column_medium_43 {width:auto !important;}
    .b-atop,
    .box_block,
    .branding-p,
    .p-directhack,
    .daynews__spring,
    .p-directhack > div,
    .cols__column_asd,
    .sticky__inner_bottom,
    .p-directhack_240x400,
    .cols__column_news240x400,
    [style="color:#fff;background-color:#4A76A8;background-size:auto 40px"],
    [style="color:#000000;background-color:#EEEFFF;background-size:auto 40px"] {display:none;}
}
