style.css:
@-moz-document url-prefix("https://panzoid.com/preview/clipmaker") {
.clip {
    height: 100%;
    background-color: #2d8055;
    border: 1px solid #232323;
    box-sizing: border-box;
    position: absolute;
    cursor: default;
    overflow: hidden;
    border-left-color: #838383;
    border-top: 0;
    border-bottom: 0;
}

.clip.selected {
    background-color: #2da759;
}

.editorpanel {
    overflow: hidden;
    border: 1px solid #6f6f6f;
    box-sizing: border-box;
    position: absolute;
    outline: 0;
    background-color: #2a2a2a;
}

.pzinput {
    padding: 2px;
    border: 1px solid transparent;
    color: #3ec17d;
    cursor: move;
}

.pz-inputbox {
    border: 1px solid #3ec17d;
    background-color: #1a2f24;
    font-family: inherit;
    color: #fff;
    border-radius: 0;
    box-sizing: border-box;
}

ul.tree li.selected {
    background-color: #2d8055;
}

ul.tree li {
    padding: 3px 0;
    cursor: default;
    border-bottom: 1px solid #353535;
    display: grid;
    white-space: nowrap;
    box-sizing: border-box;
    grid-column-gap: 5px;
}

.elevator a.selected {
    background-color: #2d8055;
}

.button {
    display: inline-block;
    padding: 0 7px;
    background-color: #2d8055;
    color: #e9ecef!important;
    text-decoration: none;
    transition: background-color .4s ease;
    line-height: 30px;
}
}
