style.css:
@-moz-document domain("ecosia.org") {
.search-section {
    background-color: #333333;
}
.logo-wrapper path:nth-child(2) {
	fill: white;
}
.description, .heading {
    color: white;
}
.button-submit {
    padding: 0.7em !important;
}
a, .form-label, .settings-headline {
    color: #c2c2c2 !important;
}
p:not(.text):not(.lead):not(.quote):not(.claim-content) {
    color: #c2c2c2 !important;
}
.typeahead, .suggestion-list {
    background-color: #242424 !important;
    border: none !important;
}
.typeahead-suggestion:not(:last-of-type), .suggestion-item:not(:last-of-type) {
    border: none !important;
}
.typeahead-suggestion:hover, .suggestion-item:hover, .suggestion-item.selected {
    background-color: #434343 !important;
}
.dropdown-menu, .dropdown.dropdown-right {
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
    background-color: #292929;
    border-radius: 1px;
    border-style: solid;
    border-width: 0 1px 0 1px;
    border-color: #9696964d;
}


.filter-trigger, .filter-trigger:hover {
    background-color: #4b4b4b;
    color: #c2c2c2;
}
.filter-list {
    background-color: #292929;
}
.flags-dropdown-body, .flags-dropdown .pill:after, .notifications-item {
    background-color: #292929;
    border: none !important;
}
.flags-dropdown-header, .flags-dropdown-more, .flags-dropdown-chosen, .flags-dropdown-arrow, .notifications-center-more {
    color: #c2c2c2 !important;
    background-color: #424242;
    border-color: #9696964d !important;
}
.flags-dropdown-list li {
    color: #c2c2c2 !important;
}
.dropdown-item:hover, .icons-dropdown-list li:hover, .flags-dropdown-list li:hover, .filter-link:hover, .notifications-item:hover {
    background-color: #434343 !important;
}
.dropdown-menu-group, .dropdown .group {
    border-color: #9696964d;
}
.entity {
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
    border: 1px solid #9696964d !important;
}
.thumbnail-video {
    background-color: #292929;
    -webkit-box-shadow: unset;
    -moz-box-shadow: unset;
    box-shadow: none;
    border-radius: 2px;
    border: 2px solid #242424; 
}
.thumbnail-video-duration {
    color: #c2c2c2;
}
.thumbnail-publisher, .result-url {
    color: #9e9e9e !important;
}
.result-url {
    padding-bottom: 1rem;
}
.entity-title {
    color: white;
    font-size: 1.6rem;
    font-weight: 500;
}
.entity-subtitle {
    color: white;
    font-size: 1rem;
    font-weight: 500;
}
.entity-disambiguation-title {
    font-size: 1.2rem;
    font-weight: 500;
}
.entity-description-more, .result-url-video {
    font-weight: 600;
}
.entity-disambiguation-item, .entity-footer {
    border-color: #515151;
}
.result-title, .result-title b {
    font-size: 1.2rem;
    font-weight: 500 !important;
}
.result-deeplink-item .result-title {
    font-size: 0.98rem;
    font-weight: 500;
}
.image-results {
    background-color: #292929;
}
.image-result {
    padding: 0.3em;
    border: none;
}
.search-header, .row, .navbar-row, .small-footer, .results-page, .result-wrapper, .body-settings, .popover-section {
    background-color: #333333;
}
.js-search-input, .input-wrapper, .input, .search-form-button, .search-form-wrapper, .buttons-wrapper {
    background-color: #292929;
    color: #e6e6e6 !important;
}
.buttons-wrapper {
    padding-right: 0;
}
.navbar-link, .navbar-link:hover, .navbar-link:visited, .navbar-link:active {
    color: #cecece;
}
.navbar-item-vertical.navbar-item-active .navbar-link:after, .navbar-item-vertical.navbar-item:hover .navbar-link:after {
    height: 2px;
    background-color: #e6e6e6;
}
.search-form-submit, .button-submit {
    margin-left: 0.5em;
}
.search-form-clear line , .button-clear path {
	stroke: rgba(255,255,255,0.5);
    fill: white;
}
.search-form-submit:hover, .button-submit:hover {
    background-color: #1f1f1f;
}
.search-form-wrapper, .search-form {
    border: 1px solid #242424 !important;
}
.search-form-submit line, .button-submit path {
    stroke: white !important;
}
.search-form-submit path, .button-submit path, .logo-anchor g path:nth-child(2) {
    fill: white !important;
}
.section-title-pill, .pill, .pill-inversed, .pill:hover, .pill-inversed:hover, .pagination-button:hover svg, .notifications-item, .notifications-centre-toggle {
    background-color: #4b4b4b !important;
    border: none;
}
.navbar-row .pill g path:nth-child(2) {
    fill: #c2c2c2;
}
.navbar-row, .disclaimer {
    border-width: 1px;
    border-color: #2c2c2c;
}
.section-title:after {
    height: 2px;
    background-color: rgba(150,150,150,0.3);
}
.pill-text, .section-title-pill {
    color: #c2c2c2;
}
.search-header-dropdown, .icon-button:not(.scroll-down):not(.button-clear) {
	padding: 5px;
    border-radius: 3px;
}
.search-header-dropdown path, .icon-button:not(.scroll-down):not(.button-clear) path, .notifications-center-toggle path, .filter-button path {
    fill: #b6b6b6;
}
.search-header-dropdown:hover, .icon-button:not(.scroll-down):not(.button-clear):not(.button-submit):hover {
    background-color: #cecece !important;
}
.search-header-dropdown:hover path, .icon-button:not(.scroll-down):not(.button-clear):hover path{
    fill: #242424;
}
.navbar-more.hidden-handheld label path {
    fill: #e6e6e6;
}
.pagination-prev, .pagination-next {
    background-image: none;
    background-color: #4b4b4b;
    border-radius: 5em;
}
.pagination-prev:hover, .pagination-next:hover {
    background-image: none;
}
.pagination-button:hover, .pagination-button:active, .pagination-button:focus {
    background-image: none;
}
.pagination-button:hover {
    border-color: #434343;
    border-radius: 5em;
    background-color: #4b4b4bd4;
}
.pagination-button:hover svg {
    background-color: transparent !important;
}
.pagination-button path {
	fill: #ffffff;
}
.pagination-button:hover path {
	fill: #c2c2c2;
}
.tag, .tag:hover {
    background-color: #4b4b4b;
    border: 1px solid #434343;    
}
.tag a, .pill .text {
    color: #e6e6e6 !important;
}
.tag:hover a {
    color: #c2c2b4 !important;
}
}
