style.css:
@-moz-document domain("lurker.pl") {
/*góra*/
.nightMode .header__firstLine, .nightMode .appHeader { background: #1d2226; border-bottom: 1px solid #141517; }

/*uberhashe*/
.nightMode .header__secondLine h5 { color: #484c53; }
.nightMode .header__secondLine a { color: #9fa3a4!important; background: #141517; border-color: #232528; }
.nightMode .header__secondLine a.isActive, .nightMode .header__secondLine a.isActive:focus { background: #2e3035; border-color: #232528; }
.nightMode .header__secondLine a:hover { border-color: #1d2226; background: #2e3035; }

body.nightMode { background: #141517; }

/*sortowanie*/
.el-dropdown-link { cursor: pointer; color: #9096a4; }
.nightMode .darkish { color: #9096a4; }

/*numeracja*/
.nightMode .el-pager li.number.active { color: #9096a4; }
.nightMode .el-pager li.number { color: #45484f; }

/*główna linia pionowa*/
.nightMode .newpost__thumbnail, .nightMode .newpost__partA.mikro { border-color: #141517; }

.newpost__thumbnail img { width: 165px; height: 115px; }

/*tytuł wpisów*/
.newpost__title { color: #d2d6d7!important; font-size: 16px; }

/*tło wpisów*/
.nightMode .bgWhite { background-color: #181d20; }

.mb { margin-bottom: 5px; }

/*pokaż więcej*/
.nightMode .mikropost__content:before, .nightMode .mikropost__image:before { background: linear-gradient(180deg, rgba(33, 42, 56, 0), #141517); border-bottom: 10px solid #141517; }

/*dodaj nowy wpis*/
.nightMode .el-button, .cta { background: #181d20; border-color: #1d2226; color: #484c53; box-shadow: 0 0px; }

.nightMode .el-button--primary:hover { background: #22292d; border-color: #1d2226; color: #000; }

/*wybierz hash*/
.nightMode .sidebar__hashSearch h5 { color: #484c53; border-bottom: 1px solid #292c31; }
.nightMode .el-button:hover { background: #22292d; border-color: #1d2226; color: #000; }

/*tło otwartego wpisu*/
.nightMode .singlePost__body { background: #181d20; color: #eaeae1; }

#postBodySingle { font-size: 16px; }

/*tło komentarzy otwartego wpisu*/
.nightMode .el-dialog__body { background: #15171a!important; }

/*linia*/
.nightMode .newcomment__info { border-color: #2d353c; }

/*dół*/
.nightMode .prefooter { color: #ccc; background: #181d20; }
.nightMode .footerSection { color: #eee; background: #141517; }
.nightMode .copyrights { background: #181d20; color: #888; }

/*mój profil*/
.nightMode .profileTop, .nightMode .bgz { background: #1c1d20; color: #eee; }

/*podgląd obcego profilu*/
.nightMode .profileOpts a, .nightMode .profileOpts a:focus, .nightMode .profileOpts a:link, .nightMode .profileOpts a:visited { background: #1c1d20; }
.nightMode .profileOpts a:hover { background: #2e3035; }

/*"Odpowiedź Administracji"*/
.replyBox { background: #2d1414; color: #b9b9b9; margin-bottom: 0px; }

/*osoby plusujące/minusujące*/
.nightMode .el-dialog__body, .nightMode .el-dialog__footer, .nightMode .el-dialog__header { background: #1d2226; }

#loader {background: #141517;}
}
