style.css:
@-moz-document url-prefix("https://treeherder.mozilla.org/") {
body {
    color: #eee;
    background-color: #222;
}
.job-list table tr:nth-child(2n) {
    background: #222;
}
.job-list table {
    background: #333;
}
.btn-dkblue, .btn-dkblue-count, .btn-dkblue-classified, .btn-dkblue-classified-count, .btn-dkblue-count:hover, .btn-dkblue-classified-count:hover {
    color: lightblue;
}
.btn.group-symbol {
    color: #eee;
}
#summary-panel {
    background-color: #222;
}
ul.failure-summary-list li {
    background-color: #222;
}
.failure-summary-bugs a {
    color: #297fdc;
}
.get-next {
    background-color: #222;
}
.react-lazylog {
    background: #222 !important;
    color: #eee !important;
}
.error-line:hover {
    background-color: #444;
}
#pinboard-contents {
    background-color: #444;
    color: #eee;
}
#pinboard-related-bugs .content, #pinned-job-list .content {
    background-color: #222;
}
}
