@-moz-document url-prefix("https://www.insecam.org"), url-prefix("http://www.insecam.org") {
/* ==UserStyle==
@name         Insecam :: Dark Night Watcher. Black Theme Style.
@description  Insecam Dark Black CSS Theme. Perfect to watch without eye strain
@version      1.0
@author       David R. Mier
@homepageURL  https://www.behance.net/DrMier
@supportURL   Please write on the comments sections.
@license      Attribution-NonCommercial-NoDerivatives 4.0 International
==/UserStyle== */


/* Main Body Background Control */
body {
    font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
    font-size: 15px;
    line-height: 1.42857143;
    color: #aaa;
    background-color: #000 !important;
}

/* Section Titles */
h1, .h1 {
    font-size: 25px;
}

/* Main separator from Title Section to Thumbnails */

hr {
    margin-top: 22px;
    margin-bottom: 22px;
    border: 0;
        border-top-width: 0px;
        border-top-style: none;
        border-top-color: currentcolor;
    border-top: 1px solid #575757;
}



/* a link Main Properties */
a {
    color: #00c4ff;
    text-decoration: none;
}

a:hover, a:focus {
    color: #8c8c8c;
    text-decoration: underline;
}



/*Main Menu Hover*/
.nav > li > a:hover, .nav > li > a:focus {

    text-decoration: none;
    background-color: #444;
}

/* Main Menu Open :: Titles ::*/
.nav .open > a, .nav .open > a:hover, .nav .open > a:focus {
    background-color: #333;
    border-color: #337ab7;
}

/* Open Menu :: Content ::*/
.dropdown-menu {
    position: absolute;
    top: 100%;
    left: 0;
    z-index: 1000;
    display: none;
    float: left;
    min-width: 160px;
    padding: 5px 0;
    margin: 2px 0 0;
        margin-top: 2px;
    list-style: none;
    font-size: 16px;
    text-align: left;
    background-color: #444;
    border: 1px solid #ccc;
    border: 1px solid rgba(0,0,0,0.15);
    border-radius: 4px;
        border-top-left-radius: 4px;
        border-top-right-radius: 4px;
    -webkit-box-shadow: 0 6px 12px rgba(0,0,0,0.175);
    box-shadow: 0 6px 12px rgba(0,0,0,0.175);
    -webkit-background-clip: padding-box;
    background-clip: padding-box;
}

.dropdown-menu > li > a {
    display: block;
    padding: 3px 20px;
    clear: both;
    font-weight: normal;
    line-height: 1.42857143;
    color: #aeaeae;
    white-space: nowrap;
}

/* Main Menu Open :: Content Hover ::*/
.dropdown-menu > li > a:hover, .dropdown-menu > li > a:focus {

    text-decoration: none;
    color: #00c4ff;
    background-color: #5d5d5d;
}


/* Thumbnail Containers */

.thumbnail-item {
    text-align: center;
    padding: 5px;
    border: 1px solid rgba(95, 95, 95, 0.5);
    border-radius: 5px;
    margin-bottom: 5px;
}

/* Thumbnail Titles */
.thumbnail-item__caption {
    height: 20px;
    overflow: hidden;
        overflow-x: hidden;
        overflow-y: hidden;
}


/* Thumbnail Properties */
.thumbnail-item__img {
    margin: 0 auto 25px;
    border-radius: 0px;
    width: 300px;
    height: 300px;
}



/* Middle Content on Main Page */
.jumbotron {
    padding-top: 30px;
    padding-bottom: 30px;
    margin-bottom: 30px;
    color: inherit;
    background-color: #000;
}


/* Google Search Bar */
.gsc-control-cse {
    font-family: Arial, sans-serif;
    border-color: #333;
    background-color: #111;
}

input.gsc-input, .gsc-input-box, .gsc-input-box-hover, .gsc-input-box-focus {
    border-color: #3f3f3f;
}

html body div.container-fluid div.google-search div#___gcse_0 div.gsc-control-cse.gsc-control-cse-en div.gsc-control-wrapper-cse form.gsc-search-box.gsc-search-box-tools table.gsc-search-box tbody tr td.gsc-input div#gsc-iw-id1.gsc-input-box table#gs_id50.gstl_50.gsc-input tbody tr td#gs_tti50.gsib_a input#gsc-i-id1.gsc-input {
    background-color: #3f3f3f !important;
}

table.gsc-search-box td {
    vertical-align: middle;
    background-color: #3f3f3f;
}





/* Social Media Buttons */ 
.at-style-responsive .at-share-btn {
    padding: 0px;
}

.at-resp-share-element .at-share-btn {
    display: inline-block;
    zoom: 0;
    margin: 0 2px 0px;
        margin-top: 0px;
        margin-right: 2px;
        margin-bottom: 0px;
        margin-left: 2px;
}

.at-resp-share-element {
    height: 0px;
}

/* Page Navigation Height Reduction */
.navigation {
    height: 65px;
}

/* Page Navigation STyle */
.pagination > li > a, .pagination > li > span {
    position: relative;
    float: left;
    padding: 6px 12px;
    line-height: 1.42857143;
    text-decoration: none;
    color: #58a6ea;
    background-color: #3f3f3f;
    border: 1px solid #262626;
    margin-left: -1px;
}
.pagination > .disabled > span, .pagination > .disabled > span:hover, .pagination > .disabled > span:focus, .pagination > .disabled > a, .pagination > .disabled > a:hover, .pagination > .disabled > a:focus {
    color: #fff;
    background-color: #337ab7;
    border-color: #3686cb;
    cursor: not-allowed;
}


/* Ads Removals */

/* Top Ad Browser*/
html body div.container-fluid div.textcenter div.textcenter ins.adsbygoogle{
    display: none !important;
}

/* Top Ad Cam Viewer */
html body div.container-fluid div.textcenter main article div.grid-container ins.adsbygoogle{
    display: none !important;
}

html body div.container-fluid div.google-auto-placed ins.adsbygoogle.adsbygoogle-noablate{
    display: none !important;
}


/* Top Ad Cam Viewer Social height fixer*/
.at-resp-share-element {
    height: 40px;
}

/* Bottom Ad */
html body div.container-fluid div.textcenter div.textcenter ins.adsbygoogle ins#aswift_2_expand ins#aswift_2_anchor iframe#aswift_2{
    display: none;
}

/* Bottom Array Ad */
html body div.container-fluid div.textcenter div.textcenter ins.adsbygoogle ins#aswift_3_expand ins#aswift_3_anchor iframe#aswift_3{
    display: none;
}
}
