style.css:
@-moz-document domain("gematsu.com") {
/*Background site color*/
body {
    font-family: Arial, Helvetica, sans-serif;
    background: #101010;
    margin: 0;
    padding: 0;
    font-size: 16px;
    color: #2a2a2a;
}


/*Frontpage News Font Color*/
#updates .details .title a, #updates .details .title a:visited {
    color: #b2b1b1;
}
#updates .label .left {
    color: #a4a0a0;
}
#updates a.playstation-vita, #updates a.playstation-3, #updates a.playstation-4, #updates a.playstation-5, #updates a.psp, #updates a.ps2 {
    color: #5d318a;
}


/*Twitter on Frontpage*/
.timeline-Widget {
    max-width: 1200px;
    background-color: #1e1e1e;
    border-radius: 5px;
}
.timeline-Tweet:hover {
    background-color: rgba(115, 159, 235, .12);
}

.timeline-Header-title {
    font-size: 21px;
    font-weight: 300;
    line-height: 24px;
    color: #2E82BB;
}



/*Post & font color */
#main {
    background: #101010;
    padding: 20px;
    position: relative;
}
#post .title {
    font-size: 26px;
    color: #e2e2e2;
    font-weight: bold;
    line-height: 34px;
}
body {
    font-family: Arial, Helvetica, sans-serif;
    font-size: 16px;
    color: #bdbdbd;
}


/*Post & font color */
#poststream .label span {

    background: #b0b0b3;
    padding: 0 5px 0 0;
}

#poststream a, #poststream a:visited {

    color: #5466ab;
    font-weight: normal;
}

#post .entry blockquote {
    background: #240707;
        background-color: rgb(36, 7, 7);
    border-left: 3px solid #a80000;
  
}



/*----------------------------- */
/*comment area */

#comments {
    color: #333333;
    padding: 20px;
    line-height: 24px;
    background: #101010;
    position: relative;
}

#disqus_thread {
    color: #000;
    float: left;
    background: #1e1e1e;
    padding: 20px;
    width: 560px;
    border-top: 3px solid #009fff;
}



}
