style.css:
@-moz-document domain("zooqle.com") {
::selection{background:#7A8299;color:#fff;}
:focus{outline:0!important;outline:none!important;}
::-webkit-scrollbar-track{-webkit-box-shadow:inset 0 0 6px rgba(0,0,0,0.3);background-color:#131313!important;}
::-webkit-scrollbar{width:4px!important;}
::-webkit-scrollbar-thumb{background-color:#131313!important;border-radius:0!important;}
* {
  text-shadow: none !important;
  border-radius:0!important;
}

/* When the screen is less than 20px wide, hide the left sidebar & center the timeline. */
@media only screen and (max-width: 820px) {
.navbar-default .navbar-collapse, .navbar-default .navbar-form {border: none;}
.navbar-form {box-shadow: none;}
.navbar-form {margin-top: 0px;margin-right: 0px;}
#body_container > .row > .col-md-9 {width: 97% !important;margin-left: 1%!important;margin-top: 0.5%!important;}
.navbar-form {margin-top: 0px!important;box-shadow: none!important;}
.navbar-default .navbar-collapse, .navbar-default .navbar-form {border-color: transparent!important;}
.navbar-header {margin-left: 0 !important;}
    
}



body {
  font-family: trebuchet ms !important;
  color: #999 !important;
  background-color: #141D26 !important;
}
a, h4 {
  color: #999 !important;
  font-weight: bold !important;
}
a:hover, .col-md-12, .rm-trigger:hover {
  color: #8798A5 !important;
}
 #inputSearchTop {
  color: #ddd !important;
  border: none !important;
  background-color: #1B2836 !important;
}

.panel-body, .list-group-item {
  background-color: #11131A !important;
  color: #ddd !important;
  border: none !important;
}
.navbar-default {
  border: none !important;
  background-color: #11131a !important;
  background-image: none !important;
}
#tvbox, #movbox {
  border: none !important;
  background-color: #11131a !important;
  background-image: none !important;
}
#tvbox a:not(.btn), #movbox a,
.table-torrents a, .list-group-item a
/*, #dlPanel a*/ {
  color: #999 !important;
}
#tvbox a:hover:not(.btn), #movbox a:hover,
.table-torrents a:hover, .navbar-brand:hover,
footer a:hover, .text-center a:hover,
#tvlist a:hover, .list-group-item a:hover
/*, #dlPanel a:hover*/ {
  color: #ccc !important;
}
.table-hover>tbody>tr:hover {
  color: #666 !important;
  background-color: #ddd !important;
}
.text-nowrap /*, .nav>li>a:hover*/ {
  background: none !important;
}
.cell {
    border: none !important;
    border-radius: 0px !important;
}
.nav-pills.compact>li.active>a {
  background-color: #1b2836 !important;
  color: #7A8299 !important;
}
.badge-green {
  background: #2E7D32 none !important;
  font-size: 11px;
}
.badge-grey {
    background: #353840 none !important;
}
.prog-blue {
    background: #1da1f2 none !important;
}
.prog-yellow, .prog-green, .prog-blue, .prog-red {
    color: #141D26 !important;
}
.text-muted {
    color: #9E9E9E !important;
}
.prog-green {
    background: #81C784 none !important;
}

.prog-yellow {
    background: #E4C07E none !important;
}
.btn-success {
    color: #11131A !important;
    background: #4CAF50 !important;
    border-color: #131313 !important;
}

.btn-success .zqf-star-f {
    line-height: 9px !important;
}

.zqf.zqf-star-e {
    line-height: 16px;
}

.btn-success:hover, .btn-success:focus {
    background-color: #419641!important;
    color: #11131A !important;
}

.btn-default:active, .btn-default.active, .btn-default:hover, .btn-default:focus {
    background-color: #419641;
    border-color: #131313;
    color: #11131A !important;
}

.trans60, .badge.trans50 {
  opacity: 1 !important;
}
.imdb_widget:hover span {
  color: inherit !important;
}
.table-torrents tr td {
  border-color: #131313 !important;
}
.modal-header,
.modal-footer,
.navbar .divider-vertical {   
border: none !important;    
}   
#inputSearchTop {   
  border-top-left-radius: 4px !important;   
  border-bottom-left-radius: 4px !important;    
}
.navbar-header .btn,    
.navbar-header .form-control,
.modal-content  {   
  background-color: #141D26 !important;    
}
.btn-primary {
  border-color: #131313 !important;
  background-color: #555 !important;
  background-image: none !important;
}
.btn-primary:hover {
  background-color: #444 !important;
}
#logo {
  background-image: url(data:image/png;base64,);
}
#body_container>.row>.col-md-9 {
  width: 78% ;
  background: #11131A;
  margin-left: 11%;
}
#sidebar {
  display: none !important;
}


.cell:hover {
    background: none;
}

.rm-trigger:hover {
    background: none;
    color: #8798A5 !important;
}

.nav>li>a:hover, .nav>li>a:focus {
    background-color: #141D26;
}

.dropdown-menu {
    background-color: #141D26;
    margin-top: 8px !important;
}

.dropdown-menu>li>a:hover, .dropdown-menu>li>a:focus {
    background-color: #101010;
    background-image: -webkit-linear-gradient(top,#101010 0,#101010 100%);
    background-image: -o-linear-gradient(top,#f5f5f5 0,#e8e8e8 100%);
    background-image: -webkit-gradient(linear,left top,left bottom,from(#101010),to(#101010));
    background-image: linear-gradient(to bottom,#101010 0,#101010 100%);
    background-repeat: repeat-x;
}

.navbar-default .navbar-nav > .open > a, .navbar-default .navbar-nav > .active > a {
    background-image: none!important;
    box-shadow: none!important;
    background: #11131a !important;
    margin-top: 2px;
    padding: 13px 0 0 0;
}

.zq-panel>.panel-heading {
    top: 0px;
}

.panel-heading>.zqf {
    line-height: 18px;
    padding-right: 3px;
}

.zq-panel > .panel-heading {
    font-size: 13px;
    background: #11131a !important;
    padding: 1px 0 1px 5px;
    margin: 6px 0 0 0;
}

footer.navbar-fixed-bottom {
    display: none;
}

.dropdown-header {
    display: none;
}

label.rm-trigger {
    border-radius: 0px;
}

.sug {

    border: none !important;

}

.suglist {
    overflow-x: hidden;
    white-space: inherit;
}

.suglist > li {

    float: none;
    display: inline-block;
    width: 19.8%;

}

.panel.panel-default.eplist {
    background: #11131A;
}

.inner {
    border-top: 1px dashed #101010!important;
}

.tornum {
    border-left: 1px dashed #101010!important;
}

.table-hover>tbody>tr:hover {
    background-color: transparent !important;
}

#body_container {
	min-height: 0;
    padding-top: 53px;
}

.btn-default {
    background: #353840;
    border: 1px solid #1D2029;
}

.btn-default:hover {
    background-color: #4CAF50;
    border: 1px solid #1D2029;
    color: #141c25 !important;
}

a[title="Advanced search"] {
    padding: 7px;
}

.btn-group.pad-l.hidden-xs.hidden-sm {
    margin-left: 1px!important;
}

.nav-tabs>li.active>a, .nav-tabs>li.active>a:hover, .nav-tabs>li.active>a:focus {
    background-color: #141D26;
}

.zq-tabs .nav-tabs>li.active>a {
    border: 1px dashed #11131A;
}

.nav-tabs>li>a:hover {
    border: 1px solid #1B1B1B;
    border-bottom: inherit;
}

.zq-tabs .tab-content {
    border: 1px dashed #11131A;
}

.zq-tabs .nav-tabs {
    border-bottom: 1px dashed #11131A;
}

.table-data tr:not(:first-child) {
    border-top: 1px dashed #11131A !important;
}

#movs h4 {
    border-bottom: 1px dashed #11131A!important;
}

#actor {
    background-color: #11131A;
}

.table-objects tbody tr {
    border-top: 1px dashed #11131A!important;
}

.table-objects thead tr {
    border-bottom: 1px solid #101010!important;
}

.btn-group.pull-right.trans60 i.zqf.zqf-small.zqf-star-f {
    position: relative;
    top: -4px;
    left: -1px;
}

i.zqf.zqf-small.zqf-rss {
    position: relative;
    top: -3px;
}

.wtfbutton {
    font-size: 12px;
    margin: 0 0 0 8px;
    text-decoration: none !important;
}

.comment.odd {
    background-color: #11131a;
}

.btn-default {
    background: #141D26;
    border: 1px solid #131313;
    color: #8798A5;
}

.form-control {
    color: #8798A5;
    background-color: #11131a;
    border: 1px solid #131313;
}

.pagination > .disabled > span, .pagination > .disabled > span:hover, .pagination > .disabled > span:focus, .pagination > .disabled > a, .pagination > .disabled > a:hover, .pagination > .disabled > a:focus {
    background-color: #141d26;
    border-color: #141d26;
}

.pagination > .active > a, .pagination > .active > span, .pagination > .active > a:hover, .pagination > .active > span:hover, .pagination > .active > a:focus, .pagination > .active > span:focus {
    background-color: #1B2836;
    border-color: #1B2836;
}

.pagination > li > a, .pagination > li > span {
    background-color: #141d26;
    border: 1px solid #141d26;
}

.pagination > li > a:hover, .pagination > li > span:hover, .pagination > li > a:focus, .pagination > li > span:focus {
    background-color: #1b2836;
    border-color: #1b2836;
}

.input-group-addon {
    background-color: #11131a;
    border: none;
}

input, button, select, textarea {
    background: #11131a;
    border: none;
}

.btn-default, .btn-primary, .btn-success, .btn-info, .btn-warning, .btn-danger {
    text-shadow: none;
    box-shadow: none;
}

#scalendar {
    border-top: #1b2836 dashed 1px;
}

#cweek td {
    border-right: #141d26 dashed thin;
    border-bottom: #141d26 dashed thin;
}

.today {

    background-color: #11131a;

}

.cdt {
    background: #141d26;
}

.cwend, .cwend > .cdt {
    background-color: #11131a;
}

#tvlist {
    border-bottom: 1px dashed #1b2836 !important;
}

.navbar-default .navbar-collapse, .navbar-default .navbar-form {
    margin-left: 30px;
}

.tt-menu {
    background-color: #141d26;
    margin-top: 7px !important;
}

.tt-suggestion:hover, .tt-suggestion:focus {
    color: #fff;
    background-color: #11131a;
    cursor:pointer;
}

.tt-suggestion.tt-cursor {
    color: #fff;
    background-color: #11131a;
}

.form-control[disabled], .form-control[readonly], fieldset[disabled] .form-control {
    background-color: #141d26;
    border: none;
}

.comment, .comment.odd {
    background: #141d26 !important;
    border-bottom: 1px solid #11131a;
}

.uname {
    background: #11131a;
}

.comment.odd[style*="margin-left: 40px"] {
    margin-left: 0 !important;
    padding-left: 42px;
}

#textComment {
    border: 1px solid #141d26;
}

.newStrip {
    background: #BE1E2D;
    right: 0;
    top: 0px;
    -webkit-transform: rotate(0deg);
    opacity: 0.9;
    font-size: 10px;
}

.txt-title {
    font-size: 14px!important;
    color: #A9894F !important;
}

.txt.text-trunc div {
    font-size: 13px!important;
}

.list-group-item a[data-toggle="collapse"] {
    color: #668C47 !important;
}

.collapsed {
    color: #A9894F !important;
}

.navbar-header {
    margin-left: 9.8% !important;
}
}
