style.css:
@-moz-document domain("iichan.hk") {
/* Глобальное */
html, body {
	background-color: hsl(0, 0%, 8%);
	color: hsl(0, 0%, 66.6%);
	font-family: sans-serif;
	padding: 0px 4px;
}

a {
	color: hsl(240, 66.6%, 66.6%);
}

a:hover {
	color: hsl(0, 66.6%, 66.6%);
}

a:visited {
	color: hsl(270, 66.6%, 66.6%);
}


/* Предварительные фиксы */
.greeting, .news, .logo, .logo > a, .sublogo, .sublogo > a, .header, .list div a, .postblock, textarea, input, button, .reply, .highlight, .spoiler:hover, .managehead {
	color: inherit !important;
}


/* Меню навигации */
.adminbar {
	background-color: hsl(0, 0%, 10%);
	box-shadow: hsla(0, 0%, 25%, 0.4) 0px 1px 4px 0px;
	margin-right: -8px;
	padding: 4px;
	width: calc(100% + 8px);
}


/* Баннер */
.logo {
	padding-top: 4px;
}


/* Форма */
.theader {
	background-color: hsl(0, 0%, 20%);
}

.postblock {
	background: hsl(0, 0%, 20%);
}

textarea, input[type='text'], input[type='password'] {
	background-color: hsl(0, 0%, 5%);
	border: 1px solid hsl(0, 0%, 20%);
	border-radius: 2px;
	padding: 4px;
}

button, input[type='button'], input[type='submit'], input[type='reset'] {
	background-color: hsl(0, 0%, 15%);
	border: 1px solid hsl(0, 0%, 25%);
	border-radius: 2px;
	margin: 1px;
	padding: 4px 16px;
}

button:hover, input[type='button']:hover, input[type='submit']:hover, input[type='reset']:hover {
	background-color: hsl(0, 0%, 10%);
	color: hsl(0, 0%, 90%);
}

button:active, input[type='button']:active, input[type='submit']:active, input[type='reset']:active {
	background-color: hsl(0, 0%, 80%);
	color: hsl(0, 0%, 5%) !important;
}

input[type='checkbox'], input[type='radio'], input[type='file'], select {
	filter: invert(100%) grayscale(100%);
}

#captcha {
	filter: grayscale(100%) invert(40%);
}

.prohibition {
	color: hsl(0, 75%, 50%);
}

/* Посты */
.doubledash {
	display: none;
}

form > div > table + table {
	margin-top: 8px;
}

.reply {
	background-color: hsl(0, 0%, 16%);
	border-radius: 2px;
	box-shadow: hsla(0, 0%, 25%, 0.4) 0px 1px 4px 0px;
}

.filetitle, .replytitle {
	color: hsl(5, 25%, 50%);
}

.postername, .commentpostername {
	color: hsl(150, 25%, 50%);
}

.adminname {
	color: hsl(0, 75%, 50%);
}

.postertrip {
	color: hsl(165, 25%, 50%);
}

.unkfunc {
	color: hsl(120, 25%, 50%);
}

.spoiler, .spoiler:hover {
	background-color: hsl(0, 0%, 25%);
	color: hsla(0, 0%, 0%, 0);
}


/* Главная */
.news {
	background: hsl(0, 0%, 20%);
}

.header {
	background: hsl(0, 0%, 20%);
}

.category {
	background: hsl(0, 0%, 10%);
}

.list {
	border: none;
}


/* Панель управления */
.passvalid {
	background-color: hsl(0, 0%, 20%);
}

table[align='center'] td, table[align='center'] th:not([colspan]) {
	border-color: hsl(0, 0%, 20%);
}

.managehead {
	background: hsl(0, 0%, 10%);
}

.row1, .row2 {
	background-color: hsl(0, 0%, 16%);
	color: hsl(0, 0%, 60%);
}

form .row2 {
	background-color: hsl(0, 0%, 4%);
	color: hsl(0, 0%, 80%);
}

.passvalid ~ form > table tr > td:nth-child(3) a {
	color: hsl(5, 25%, 50%);
}

.passvalid ~ form > table tr > td:nth-child(4) {
	color: hsl(150, 25%, 50%);
}


/* Куклоскрипт */
.de-new-post, #de-popup-upload {
	box-shadow: hsla(120, 50%, 50%, 0.4) 0px 1px 4px 0px !important;
	background-color: hsl(120, 8%, 16%);
}

.de-post-removed, #de-popup-delete {
	box-shadow: hsla(0, 50%, 50%, 0.4) 0px 1px 4px 0px !important;
	background-color: hsl(0, 8%, 16%);
}

.de-selected, #de-popup-newposts {
	box-shadow: hsla(240, 50%, 50%, 0.4) 0px 1px 4px 0px !important;
	background-color: hsl(240, 8%, 16%);
}

.de-pview {
	box-shadow: hsla(0, 0%, 0%, 0.4) 0px 2px 6px 0px;
}

#de-wrapper-popup, .de-popup, .de-pview {
	border: none !important;
	margin: 4px !important;
}

.de-post-counter {
	font: inherit;
	font-size: 75%;
	font-weight: bold;
	color: hsl(0, 0%, 50%);
}

.de-post-counter-deleted {
	color: hsl(0, 100%, 75%);
}
}
