style.css:
@-moz-document url-prefix("https://www.programcreek.com/python"), url-prefix("https://www.programcreek.com/java-api-examples"), url-prefix("https://www.programcreek.com/scala") {
html, body {
  background-color: #222;
  color: #ccc;
}

/******** Python ********/

/****** Example Page e.g. www.programcreek.com/python/example/101232/ ******/

/** Header **/
h1[style*="color:#333333;"] {
  color: #aaa !important;
}

/*** Code Box ***/

.examplebox {
  background-color: #282828;
  border-color: #333;
}

/* Header */
.exampleboxheader {
  background-color: #333;
  border-bottom-color: #333;
}

/* Vote buttons */
.click_button {
  background-color: #282828;
  border-color: #333;
}

.click_button:hover {
  background-color: #666;
}

span[style*="border:1px solid #d8d8d8;"] {
  border-color: #333 !important;
}

/*** Syntax Highlighting ***/

/* Normal */
.pln {
  color: #ccc;
}

/* for */
.kwd {
  color: #4fa2b3; /* from cppreference; #008; */
}

/* Literals */
.lit {
  color: #7aa3c3; /* from cppreference; #066; */
}

/* Type */
.typ {
  color: #7aac18; /* from PyTorch; #606; */
}

/* Comments */
.com {
  color: #666; /* from cppreference; #800; */
}

/* Highlighted term */
.highlight {
  background-color: #34383c; /* from VS Code selected term highlighting - #282b2e  */
}

/* Ads */
/* div[id*="google_ads"] {
  display: none !important;
} */

/*** 'Related Functions' Box on right ***/

.sidebarbox {
  background-color: #282828;
  border-color: #333;
}

.sidebarboxheader {
  background-color: #333;
  border-bottom-color: #333;
}

#sidebar-example {
  color: #aaa;
}

.sidebarbox li {
  list-style-image: none;
  list-style-type: disc;
}

/****** Popular Modules Page e.g. www.programcreek.com/python/index/module/list ******/


/* Search icon */
img[src="https://www.programcreek.com/python/includes/images/search.png"] {
  filter: invert(86.5%);
}

/* Serial No. */
#api-list-apiname {
  color: #999; /* made less bright */
}

ul#api-list li {
  border-bottom-color: #333;
}

ul#paging li {
  background-color: #282828;
  border-color: #333;
}

ul#paging li:hover {
  background-color: #444;
}

ul#paging li:hover a {
  color: #5291f8; /* from cppreference */
}


/****** Search Page e.g. www.programcreek.com/python/ ******/

/* Logo */
img[src="./includes/images/python-logo.png"] {
  filter: invert(86%);
}

/* Search box */
input[name="ClassName"] {
  background-color: #282828 !important;
  border-color: #333 !important;
}

/* 'Search' button */
input[name="submit"] {
  background-color: #222 !important;
  border-color: #333 !important;
  color: #ccc !important;
}

input[name="submit"]:hover {
  background-color: #666 !important;
}

/* Tag list */
a.tag {
  background-color: #333;
  border-color: #333;
  color: #ccc;
}

a.tag:hover {
  background-color:#02427A; /* from #header */
  border-color: #333;
  color: #ccc;
}

/****** Search Results e.g. www.programcreek.com/python/index/513/numpy.random ******/

span[style="color:blue;"] {
  color: #5291f8 !important; /* from cppreference */
}

/****** Saved Python APIs/Your Account Page - www.programcreek.com/python/?action=home_page ******/

img[alt="Program Creek"] {
  filter: invert(86.5%);
}

/****** Login Page - www.programcreek.com/python/?action=login ******/

header[style*="border-bottom: 1px solid #ccc;"] {
  border-bottom-color: #666 !important;
}

#loginArea {
  background-color: #282828;
  border-color: #333;
  color: #aaa;
}

#loginArea input[type="text"], #loginArea textarea, #loginArea input[type="password"] {
  background-color: #222;
  border-color: #333;
}

#loginArea input[type="text"]:focus, #loginArea textarea:focus, #loginArea input[type="password"]:focus {
  border-color: #85b1de;
}


/******** Java ********/

/****** 'Top Java Packages' Page e.g. www.programcreek.com/java-api-examples/index.php?action=top_java_packages ******/


div[style*="background-color: #eaf6fc;"] {
  background-color: #333 !important;
  border-color: #444 !important;
}

ul#api-list li:hover {
  background-color: #333;
}

div[style*="background-color: #f7f9fc;"] {
  background-color: #333 !important;
  border-color: #444 !important;
}
}
