style.css:
@-moz-document domain("www.hulu.com") {
.controls__progress-bar-played, .controls__progress-bar-total, .controls__setting-slider, .cu-levelone, .controls__volume-max, .controls__volume-now {
	color: #fff;
	background: linear-gradient(-45deg, #EE7752, #E73C7E, #23A6D5, #23D5AB);
	background-size: 400% 400%;
	-webkit-animation: Gradient 15s ease infinite;
	-moz-animation: Gradient 15s ease infinite;
	animation: Gradient 15s ease infinite;
}
@-webkit-keyframes Gradient {
	0% {
		background-position: 0% 50%
	}
	50% {
		background-position: 100% 50%
	}
	100% {
		background-position: 0% 50%
	}
}
@-moz-keyframes Gradient {
	0% {
		background-position: 0% 50%
	}
	50% {
		background-position: 100% 50%
	}
	100% {
		background-position: 0% 50%
	}
}
@keyframes Gradient {
	0% {
		background-position: 0% 50%
	}
	50% {
		background-position: 100% 50%
	}
	100% {
		background-position: 0% 50%
	}
}
.controls__progress-bar-total, .controls__volume-max {
	opacity: 0.2;
	}
.controls-wrap, .GenericTileContent__action, .SimpleCollection__description, .Tile__action {
	color: #FFFFFF;
	}
.ViewAllLink__btn-text {
    color: #E5E5E5;
	}
.GlobalNav--transparent, .GlobalNav {
	background: var(--background--nav);
	box-shadow: 0 1px 0 0 var(--accent--nav);
	color: var(--text--primary);
	transition: background-color .75s ease,box-shadow .75s ease;
    opacity: 0.98;
    }
.BrowseMenu__item {
    color: rgba(255,255,255,0.85);
    }
.BrowseMenu__menu-container {
    background: --background--nav-dropdown;
	background-color: var(--background--nav);
    }
}
