style.css:
@-moz-document url("https://www.gamestar.de/") {
#header, #foot {
    background: #004ca0;
}

#header .service {
    background: #003b7a;
    color: white;
    padding: 5px 10px;
}

#header .service a {
    color: white;
    text-decoration: none;
}

#header .logo span {
    background: url(https://www.gamestar.de/gs_cb/assets/core/images/GS_logo_white.svg) 0 0 no-repeat;
}

#header .service .network {
    border: none;
}

#header .service .menulist li .sub {
    background: #003b7a !important;
    border: none;
}

#header .service .menulist li .sub,
#header .login .avatarmenu,
#header .navi .menulist li .sub {
    box-shadow: 4px 4px 5px rgba(0,0,0,.3);
    -webkit-box-shadow: 4px 4px 5px rgba(0,0,0,.3);
    -moz-box-shadow: 4px 4px 5px rgba(0,0,0,.3);
}

.service .bottomline {
    border-bottom: none;
    height: 5px;
}

.subnavicontent .navilist a:hover {
    background-color: #26a65b;
}

.homeHighlight,
.homeContReg,
.homeContRegLast,
.cornerSTop {
    border-radius: 0;
    border: none;
}

#lbSpacer, #dfp-spacer {
    display: none;
}

.menuitem {
    border: 1px solid transparent !important;
}

#header .login .avatarpic img {
    border-color: transparent;
    border-radius: 50%;
    width: 40px;
}

#header .login .avatarpic {
    margin: -8px 0 0 0;
}

#header .login div.inactive a {
    margin-right: 10px;
}

#header .login .userInfo {
    display: none;
}

.main.cornerSTop {
    float: right !important;
    margin-right: 2px !important;
}

#header .navi .main a.menuitem, #header .navi .home a.menuitem {
    border-radius: 0 !important;
}

#header .navi .main, #header .navi .home {
    background: #003b7a;
    font-family: "soleil",Helvetica,Arial,sans-serif;
    text-transform: uppercase;
}

.subnavicontent {
    text-transform: none;
}

li.special a {
    background: #db101a;
}

#header .navi #n303 a.menuitem {
    background: #2ac61c;
    padding: 0;
    height: 100%;
}

#header .navi a.menuitem:hover {
    text-decoration: none;
}

#header .navi #n303 a.menuitem #plus {
    height: 32px;
    width: 70px;
    margin-right: 0;
    background: url(https://www.gamestar.de/img/plus/gsp_plus_logo_white.svg) no-repeat left center;
}

#header .navi #n303:hover #plus, #header .navi #n303 a.menuitem:hover #plus, #header .navi #n303 a.active #plus {
    background: url(https://www.gamestar.de/img/plus/gsp_plus_logo_white.svg) no-repeat left center !important;
}

#header .navi #n303:hover, #header .navi #n303 a.menuitem:hover, 
#header .navi .home #n303:hover a.menuitem{
    background: #1f874a !important;
}

.menuitem div {
    font-size: 0;
}

#header .searchInput {
    border-radius: 0;
    border: none;
    background: white;
}

#header .searchButton {
    border-radius: 0;
    border: none;
}
}
