style.css:
@-moz-document domain("www.sudomemo.net"), domain("sudomemo.net")
{
body {
    background-color: #000;
}
.theme-body {
    background: #000;
}
.panel-body {
    background: #111 !important;
    color: #fff;
}
.panel-footer {
    background: #222 !important;
}
.flipnote-description {
    background: #444 !important;
    color: #fff !important;
}
.name {
    color: #999 !important;
}
.about-sudomemo {
    background-color: #444 !important;
}
.about-sudomemo p {
    background-color: #555;
}
.about-sudomemo a span {
    background-color: #555 !important;
}
.panel-heading {
    background-color: #1b661b;
    color: #fff !important;
}
.comment {
    background-color: #444 !important;
}
.panel-common {
    background-color: #444;
}
.panel-common iframe {
    filter: brightness(50%) !important;
}
.box-container {
    background-color: #444 !important;
}
.player__controls {
    background-color: #555;
}
.info-buttons input {
    background-color: #555;
}
.btn-custom {
    background-color: #444 !important;
    color: white;
    border: none;
}
.btn-custom:hover {
    background-color: #666 !important;
    color: white;
    border: none;
}
.btn-custom-active {
    background-color: #1b661b;
    border: none;
}
.btn-custom-active:hover {
    background-color: #188418;
    color: #fff;
    border: none;
}
.star-country-index {
    color: white;
}
.navbar-custom:after {
    background-color: #444 !important;
}
.player__scrubber {
    background-color: #666;
}
}
