style.css:
/*
	Created by Michał Formella (http://mform.pl)

	Note: boxofficemojo.com uses inline styles without classes,
	so this css is contains "bad habits". 
*/

@-moz-document domain(boxofficemojo.com) {

/* General colors */
body {
    background: #040016 !important;
    color: #d8d8d8 !important;
    font-family: 'Noto Sans', sans-serif !important;
}

div#header {
    border-bottom: 1px solid #000 !important;
    background: #020408 !important;
    height: 64px !important;
}

div#top_links {
    font-size: 10px !important;
    padding: 2px 8px 4px !important;
    background: #0a0a12 !important;
    color: #bbccdd !important;

}

div#leftnav {
    width: 120px !important;
}

div#main {
    width: 1032px !important;
}

nobr > small > font > font, nobr > small > font:last-child {
	color: #d8d8d8 !important;
}

div#container {
    background: #080012 !important;
    border-left: 1px solid #323264;
    border-right: 1px solid #323264;
    box-shadow: 0 0 4px #242436;
    padding: 0 2px;
}

div#hp_footer {
    color: #ececec !important;
    background-image: none !important;
    background: #020408 !important;
}

div.mp_box_tab {
    padding: 1px 4px !important;
    font-size: 16px !important;
    color: #f9ad3b !important;
}

b > span {
	background-color: #080012 !important;
}

/* Images */
div#hp_logo a img, div#copyright a img, .connect_nav img, div#main > img:first-child {
    border: none;
    filter: invert(100%);
}

/* Inputs and links */
input, select, button {
	background-color: #040020 !important;
    border: 1px solid #242448 !important;
    border-radius: 4px !important;
    color: #ececec !important;
    margin: 3px 2px 2px 0 !important;
    padding: 0.25em 0.5em !important;
    vertical-align: middle !important;
}

input[type="text"] {
	background-color: #020008 !important;
}

button, input[type="submit"], input[type="button"] {
	cursor: pointer;
}

ul.nav_tabs li.nav_tab_current {
    background-color: #080012 !important;
    color: #d8d8d8 !important;
}

ul.nav_tabs li a {
    padding: 3px 15px !important;
    border: 1px solid #323248 !important;
    background-color: #040020 !important;
}

ul.nav_tabs li.nav_tab_current a {
    background-color: #080012 !important;
    color: #d8d8d8 !important;
    border-bottom: 1px solid #080012 !important;
}

#footer_links a {
    color: #636384 !important;
}

a {
    text-decoration: none !important;
}

a:hover {
    color: #7ad4fe !important;
    text-decoration: underline !important;
}

a:link {
    color: #03affe !important;
}

a:visited {
    color: #03affe !important;
}

/* Tables */


table, tbody, tr, th, td {
    border-color: transparent ! important;
}
tr {
    box-shadow: 0 2px 1px -1px #121224 !important;
}
table, tr, td, th {
	background-color: transparent ! important;
    color: #d8d8d8 !important;
}
}
