/*----- whaleshares modification -----*/
.UserHeader {
  display: none!important; 
}

.Sidenav__active {
  display: none!important; 
} 

.Story__content__body {
  display: none!important; 
}

.anticon-flag {
    display: none!important; 
}

.QuickPostEditor__avatar {
    display: none!important; 
}


/*----- logo change and personal stamp -----*/

.logo {
  display: block;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
  background: url(https://i.imgur.com/p7Yu12H.png) no-repeat;
  width: 170px; /* Width of new image */
  height: 78px; /* Height of new image */
  padding-left: 170px; /* Equal to width of new image */
}



.UserBox__stats::after {
  content: url(https://imgp.whaleshares.io/profileimage/aschatria/64x64) "nightmode theme by @aschatria";
    padding: 5px;
    display: block;
    color: #A57164;
    background: #111111;
}




/*----- whaleshares post feed and posts modification -----*/

.PostFeedEmbed {
   width:30%!important; 
}

.Story {
    height: 300px!important;
    background: #272727!important; 
    border: none;
}

.Story__content__img-container {
    width:30%!important;   
}

.Story__content__body {
    display: none!important; 
}

.Story__content__title {
    float: right;
    width:60%!important; 
    
}

.Story__content {
    background-color:#272727!important;
    padding: 15px;
}

.StoryFull {
  background-color: #272727!important; 
    padding: 10px;
}

.Story__footer {
  
}



/*----- parts -----*/

.Write__markdown::after {
  content: " - BASIC MARKDOWN - Remember this: for titles # H1, ## H2, ### H3, **bold text**, *italicized text*, > blockquote, `code`, Horizontal Rule - 3 short dashes, Link [title](https://www.example.com), Image ![alt text](image.jpg), Linked Image [![image alt text](image.jpg)](https://www.example.com)";
}
