    padding-right: 10px;
    
}

.Comment__content {
    padding-right: 10px;
    padding-left: 10px;
    
}

.Comment {
    padding: 15px;
    background: #272727!important; 
    display: block;
    min-width:250px;
}


.p {
    padding: 15px;
    background: #272727!important;  
}


