style.css:
@-moz-document domain("iptorrents.com") {
body {
    background-color: #0A0A0A;
    color: #888;
    font-family: 'Open Sans', sans-serif;
    font-weight: bold;
}


#iptStart .banner .stats a {
    color: #999;
    margin: 5px;
    text-decoration: none;
}

#iptStart .banner .stats a:hover {
    margin: 5px;
    text-decoration: underline;
}

#iptStart .banner .stats {
    color: #999;
    padding: 10px;
    position: relative;
    text-align: center;
}

#iptStart .stats .rCol {
    position: relative;
    right: 7px;
    text-align: center;
    margin: 10px;
}


#iptStart {
    background-color: #0F0F0F;
    width: 1000px;
    margin: 0px auto;
    position: relative;
}



#iptStart .topRow>a {
    margin-top: 20px;
    border-right: 1px solid #101010;
    box-shadow: none;
    display: inline-block;
    font-size: 14px;
    font-weight: 700;
    color: #CCC;
    height: 20px;
    padding: 2px 10px;
    text-decoration: none;
    transition: all 0s;
}

#iptStart .topRow>a:hover {
    color: #E0245E;
    text-shadow: none;
    transition: all 0s;
}

#iptStart .topRow .thmT {
    margin-top: 20px;
    background: #181818;
    border: 1px solid #181818;
    border-radius: 20px;
    cursor: pointer;
    display: inline-block;
    height: 16px;
    padding: 1px;
    position: relative;
    text-align: left;
    transition: all 0.8s cubic-bezier(1, 0, 0.5, 0);
    vertical-align: top;
    width: 50px;
    z-index: 2;
}

#iptStart .topRow .thmT div {
    background: #1888CD;
    border: 2px solid #1888CD;
    border-radius: 20px;
    display: inline-block;
    height: 12px;
    left: 1px;
    position: absolute;
    transition: all 0.5s cubic-bezier(0.8, 0, 0.05, 1);
    vertical-align: middle;
    width: 12px;
    z-index: 2;
}

#iptStart .butRow a {
    display: inline-block;
    position: relative;
    top: 0px;
    vertical-align: bottom;
    text-decoration: none;
    text-align: center;
}

#iptStart .butRow a span {
    display: none;
    border-color: transparent;
    background: #000;
    color: #FFF;
    border-radius: 50%;
    height: 54px;
    width: 56px;
    transition: all .3s,box-shadow 1s;
}

#iptStart .butRow {
    background: #1888;
    border: none;
    box-shadow: none;
    color: #EEE;
    display: block;
    text-align: center;

}

#iptStart .butRow a b {
    border: none;
    box-shadow: none;
    color: #CCC;
    display: block;
    font-size: 14px;
    font-weight: 600;
    margin: 0px 0 0;
    padding: 15px 20px;
    line-height: 15px;
    text-shadow: none;
}

#iptStart .butRow a b:hover {
    border: none;
    box-shadow: none;
    color: #FFF;
    display: block;
    font-size: 14px;
    font-weight: 600;
    margin: 0px 0 0;
    padding: 15px 20px;
    line-height: 15px;
    text-shadow: none;
    background-color: #0F0F0F;
}

#toggleCatA, #toggleTopA {
    -webkit-appearance: none;
    position: relative;
    background: #101010;
    border: none;
    border-width: 0 0 1px;
    border-radius: 5px;
    color: #CCC;
    cursor: pointer;
    margin: 15px auto;
    min-width: 965px;
    max-width: 1200px;
    width: 1000px;
    padding: 10px;
    outline: none;
    opacity: 1;
    transition: none;
}

#toggleCatA:hover, #toggleTopA:hover {
    -webkit-appearance: none;
    position: relative;
    background: #101010;
    border: none;
    border-width: 0 0 1px;
    border-radius: 5px;
    color: #777;
    cursor: pointer;
    margin: 15px auto;
    min-width: 965px;
    padding: 10px;
    width: 1000px;
    max-width: 1200px;
    outline: none;
    opacity: 1;
    transition: none;
}


#toggleCatA::before, #toggleCatA::after, #toggleTopA:before, #toggleTopA:after {
    border: none;
    content: " ";
    display: block;
    height: 0;
    width: 0;
    left: 50%;
    margin: -5px -110px;
    position: absolute;
    top: 50%;
}

#toggleCatA:hover, #toggleTopA:hover {
    transition: none;
    box-shadow: none;
    margin: 15px auto;
    padding: 10px;
    width: 1000px;
    opacity: 1;
}

#Search {
    width: 1000px;
    margin: 10px auto;
    min-width: 980px;
    max-width: 1200px;
}

#Search input:first-child {
    background-color: #0F0F0F;
    width: 75%;
    border-radius: 5px 0 0 5px;
}

#Search input, #Search select {
    font-family: 'Open Sans', sans-serif;
    font-weight: bold;
    -webkit-appearance: none;
    color: #CCC;
    font-size: 17px;
    border: none;
    background: #151515;
    box-shadow: none;
    border-width: 1px 0 1px 1px;
    box-sizing: border-box;
    height: 40px;
    padding: 5px 10px;

}

#Search input[type="submit"] {
    cursor: pointer;
    border-width: 1px 1px 1px 0;
    background: #0F0F0F;
}

.single.selected {
    box-shadow: none;
    color: #E0245E;
    padding: 5px 8px;
}
.paginate .single {
    display: inline-block;
    border: none;
}

a:hover {
    color: #CCC;
    text-decoration: underline;
}

.pagination .info b {
    font-size: 16px;
    text-shadow: none;
    color: #008850;
}

.t1 th {
    background-color: #1888;
    font-size: 12px;
    font-weight: bold;
    padding: 10px 5px;
}

#torrents th {
    padding: 10px;
}

#torrents {
    width: 1000px;
    min-width: 980px;
    max-width: 1200px;
    box-shadow: none;
}

.t1 th, .t1 td {
    padding: 0 5px;
}

.t1 {
    box-shadow: none;
    background: #0F0F0F;
    border-spacing: 0;
    border: none;
    border-collapse: separate;
}

.t1 td {
    border: none;
}

.t_ctime {
    font-family: 'Open Sans', sans-serif;
    color: #777;
    float: right;
    font-size: 12px;
}

a {
    color: #CCC;
    text-decoration: none;
    font-weight: bold;
    font-size: 14px;
}

.b {
    font-weight: bold;
}

#catters label {
    background: #0F0F0F;
    border: none;
    border-radius: 3px;
    display: block;
    margin: 1px 4px;
    padding: 2px 10px 3px 29px;
    position: relative;
    box-shadow: none;
    cursor: pointer;
}

#catters label a, #catters label span {
    text-decoration: none;
    font-size: 12.5px;
}

#catters label input:checked + span::before {
    background: #008850;
}
#catters label input + span:before {
    background: #0F0F0F;
    border-radius: 7px;
    box-shadow: 0 0 3px #008850;
    content: " ";
    height: 8px;
    left: 10px;
    margin: -5px 0 0;
    position: absolute;
    top: 50%;
    width: 8px;
}

#torrents td {
    border-bottom: 1px solid #131313;
}

#iptStart .banner {
    background-image: none;
    height: 270px;
    box-shadow: none;
    background: #0F0F0F no-repeat 0 0;
    border: none;
    border-left: 0;
    border-radius: 0px;
}

#iptStart .banner .bannerPlaceholder {
    display: none;
    height: 197px;
    background-image: none;
}

#iptStart  .banner{
    background-image: none !important;
    background-color: #0F0F0F;
    height: 100px;
    border: none;
    box-shadow: none;
  }

.dlBtn {
    border: none;
    border-radius: 50%;
    font-size: 85px;
    margin: 10px;
    padding: 15px 20px;
}

.i0 {
    background-color: #101010;
    border: none;
    color: #1888BC;
    cursor: pointer;
    padding: 5px 30px;
}

.tTab tr:first-child th + td div::before {
    border: none;
    border-width: 1px 0 0 1px;
    content: " ";
    display: block;
    height: 10px;
    position: absolute;
    right: 0;
    top: -10px;
    width: 100%;
    background: none;
}

.td_fname {
    color: #008850;
    font-size: larger;
}

.tTab th + td {
    background-color: #0F0F0F;
}

.tTab th {
    background: #151515;
    border: none;
    box-shadow: none;
    color: #CCC;
    padding: 0 10px 0 20px;
    text-shadow: none;
}

.td_fname i {
    vertical-align: bottom;
    margin: 0 5px;
    position: relative;
}

#imdbInfo {
    background: #0F0F0F;
    border-radius: 7px 7px 0 0;
    border: 1px solid;
    border-color: #0F0F0F transparent #0F0F0F;
    box-shadow: 0 5px 5px -4px #000;
    position: relative;
    left: -114px;
    top: 0;
    width: 975px;
    padding: 0 0 15px;
}

.tTab .imdbTr+tr td.des {
    box-shadow: none;
}

textarea {
    background: #151515;
    border: 0;
    border-radius: 5px;
    color: inherit;
    padding: 5px;
}

.tTab td.des {
    box-shadow: none;
}

.fa-arrow-up:before {
    color: #CCC;
    content: "\f062";
}

.fa-arrow-down:before {
    color: #AAA;
    content: "\f063";
}

.fa-copy:before, .fa-files-o:before {
    color: #000;
    content: "\f0c5";
}

.fa-2x {
    font-size: 20px;
    line-height: .75em;
    vertical-align: -15%;
}

.t_seeders {
    color: #41B785;
    font-weight: 700;
}

.t_leechers {
    color: #E0245E;
    font-weight: 700;
}

cloudflare-app[app="back-to-top-button"][visibility="visible"] {
    pointer-events: all;
    opacity: 1;
    background-color: #101010 !important;
    border-radius: 5px !important;
}

.tTipWrap .tTip {
    display: none;
    background-color: #101010;
    left: 50%;
    padding: 5px 10px;
    position: absolute;
    top: 100%;
    z-index: 100;
    color: #CCC;
    white-space: nowrap;
    -moz-box-shadow: 0 0 20px #000;
    -webkit-box-shadow: 0 0 20px #000;
    -ms-box-shadow: 0 0 20px #000;
    -o-box-shadow: 0 0 20px #000;
    box-shadow: 0 0 20px #000;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    -ms-border-radius: 3px;
    -o-border-radius: 3px;
    border-radius: 3px;
    -moz-transform: translate(-50%, 7px);
    -webkit-transform: translate(-50%, 7px);
    -ms-transform: translate(-50%, 7px);
    -o-transform: translate(-50%, 7px);
    transform: translate(-50%, 7px);
}

.tTipWrap .tTip.ttop:before {
    border-bottom-color: transparent;
    border-top-color: #101010;
    top: auto;
    bottom: -16px;
}

.tTipWrap:hover .tTip {
    display: none;
}

.infoBody {
    background-color: #0F0F0F;
    box-shadow: none;
    font-size: 13px;
}

.infoHead, .infoBody .infoHead {
    padding: 10px;
    border: none;
}

#topTables table .al a i {
    color: #008860 !important;
    font-size: 17px;
    margin: 0 5px 0 0;
    vertical-align: sub;
}

.t_tag_new {
    color: #E0245E;
    font-weight: 700;
}

cloudflare-app[app="back-to-top-button"] {
    background: #101010 !important;
    border-radius: 5px !important;
    cursor: pointer;
    display: block;
    font-family: sans-serif;
    font-size: 40px;
    font-style: normal;
    font-weight: normal;
    height: 75px;
    letter-spacing: 0;
    line-height: 75px;
    position: fixed;
    text-align: center;
    text-rendering: optimizeLegibility;
    transition: opacity .3s ease-in-out;
    user-select: none;
    width: 75px;
    z-index: 7000;
}

  ::-webkit-scrollbar-track
  {
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3);
    background-color: #252525;
    border-radius: 5px;
  }

  ::-webkit-scrollbar-thumb
  {
    background-color: #1888;
    border-radius: 5px;
    background-image: -webkit-linear-gradient(90deg, rgba(0, 0, 0, .2) 25%, transparent 25%, transparent 50%, rgba(0, 0, 0, .2) 50%, rgba(0, 0, 0, 0.2) 75%, transparent 75%, transparent);
  }

  ::-webkit-scrollbar
  {
    width: 15px;
    background-color: #252525;
  }
}
