style.css:
@-moz-document domain("torrentday.com") {
body {
    background-color: #0A0A0A;
}

#navBarOuter {
    background-color: #0F0F0F;
    border-radius: 0px;
    height: 50px;
    margin: 0 auto;
    max-width: 1000px;
    /* position: fixed; */
    /* top: 0; */
    z-index: 9999;
}

#userBar {
    background: #0F0F0F;
    border-radius: 0px;
    margin: 0 auto;
    max-width: 1000px;
}

.headerColumn {
    font-size: 20px;
    display: table-cell;
    color: #CCC;
    padding: 9px;
    vertical-align: middle;
}

.menu {
    background: #1888;
    border: none;
    box-shadow: none;
    color: #CCC;
    display: block;
    margin-left: -50px !important;
    padding: 0 20px !important;
    width: 960px;
    text-align: center;
}

.menu > li {
    border: none;
    box-shadow: none;
    display: inline-block;
    font-weight: 600;
}

.menu > li:hover {
    background-color: #0F0F0F;
    border-bottom-color: #0F0F0F;
    color: #CCC;
}

.pointer:hover, .msgDiv:hover, .msgDivActive, #torrentTable tr:nth-child(odd), .topTorrentTable tr:nth-child(odd), .t0 tr:nth-child(odd), .t1 tr:nth-child(odd) {
    background: #0A0A0A;
}

.i0, #torrentTable th, .button, .btn, .topTorrentTable th, table#tvListing tr td.tvListing_header, .releaseSearch, .t0 th, .t1 th, .t2 th {
    background-color: #1888;
}

#torrents td {
    background-color: #101010;
    border-bottom: 1px solid #191919;
}

#uiSearch .wrap {
    background: #0F0F0F;
}
#uiSearch .wrap {
    display: table;
    padding: 10px;
    border-radius: 5px;
    margin: 10px 0 0;
    box-shadow: 0 0 5px #AAA;
    font-size: 18px;
    z-index: 2;
    position: relative;
}

#uiSearch:after {
    content: "";
    background: transparent;
    background: -moz-linear-gradient(left, #ff4747 0%, #ffe349 16%, #49ff7a 41%, #49b9ff 59%, #49b9ff 59%, #494fff 73%, #f549ff 87%, #ff474a 100%);
    background: -webkit-gradient(linear, left top, right top, color-stop(0%, #ff4747), color-stop(16%, #ffe349), color-stop(41%, #49ff7a), color-stop(59%, #49b9ff), color-stop(59%, #49b9ff), color-stop(73%, #494fff), color-stop(87%, #f549ff), color-stop(100%, #ff474a));
    background: -webkit-linear-gradient(left, #ff4747 0%, #ffe349 16%, #49ff7a 41%, #49b9ff 59%, #49b9ff 59%, #494fff 73%, #f549ff 87%, #ff474a 100%);
    background: -o-linear-gradient(left, #ff4747 0%, #ffe349 16%, #49ff7a 41%, #49b9ff 59%, #49b9ff 59%, #494fff 73%, #f549ff 87%, #ff474a 100%);
    background: -ms-linear-gradient(left, #ff4747 0%, #ffe349 16%, #49ff7a 41%, #49b9ff 59%, #49b9ff 59%, #494fff 73%, #f549ff 87%, #ff474a 100%);
    background-image: linear-gradient(to right, #ff4747 0%, #ffe349 16%, #49ff7a 41%, #49b9ff 59%, #49b9ff 59%, #494fff 73%, #f549ff 87%, #ff474a 100%);
    height: 50px;
    width: 1004px;
    display: none;
    position: absolute;
    top: -1px;
    left: -2px;
    -webkit-filter: blur(4px);
    -moz-filter: blur(4px);
    -ms-filter: blur(4px);
    -o-filter: blur(4px);
    filter: blur(4px);
    opacity: .8;
    transition: all .5s,background-size 3s;
    z-index: 1;
    background-size: 100%;
    background-position: 0;
}

#uiSearch .wrap {
    display: table;
    padding: 10px;
    border-radius: 5px;
    margin: 10px 0 0;
    box-shadow: none;
    font-size: 18px;
    z-index: 2;
    position: relative;
}

#uiSearch .wrap .btnWrap input {
    border-left: 1px solid #191919;
}

.uiSwitch span {
    cursor: pointer;
    display: inline-block;
    width: 100%;
    background-color: #101010;
    color: #CCC;
    font-size: 14px;
    font-weight: normal;
    text-align: center;
    text-shadow: none;
    padding: 6px 14px;
    border: none;
    box-shadow: none;
    -webkit-transition: all 0.1s ease-in-out;
    -moz-transition: all 0.1s ease-in-out;
    -ms-transition: all 0.1s ease-in-out;
    -o-transition: all 0.1s ease-in-out;
    transition: all 0.1s ease-in-out;
    box-sizing: border-box;
}

.uiSwitch input:checked + span {
    background-color: #191919;
    -webkit-box-shadow: none;
    box-shadow: none;
}

#catters {
    border: none;
    border-top: 0;
    box-shadow: none;
}

#catters .vat:last-child {
    border: none;
    box-shadow: none;
}

#catters .clrTag a:hover {
    border: none;
    background: none;
    color: #CCC;
}

#catters .clrTag a {
    font-weight: 700;
    color: #E0245E;
    border: none;
    border-radius: 3px;
    padding: 5px;
    text-align: center;
    margin: 7px 0 -5px;
    transition: all .5s;
    text-decoration: none;
    display: block;
}

.page_nav a {
    background-color: #0A0A0A;
}
.page_nav a {
    display: inline-block;
    background: none;
    box-shadow: none;
    padding: 2px 7px;
    margin: 0 2px;
    text-decoration: none;
}

.page_nav a.current {
    background: none;
    color: #E0245E;
}

.leechersInfo {
    color: #E0245E;
}

.seedersInfo {
    color: #41B785;
}

#torrents {
    box-shadow: none;
}

cloudflare-app[app="back-to-top-button"][visibility="visible"] {
    pointer-events: all;
    opacity: 1;
    background-color: #101010 !important;
    border-radius: 5px !important;
}

.topTen {
    display: none;
    table-layout: fixed;
    margin: 0 auto;
}

.topTen .red {
    color: #E0245E;
}

.topTen .grn {
    color: #41B785;
}

.bq, .quoteText, .topicPageNumSpan a, .pageNumsDiv a, .postUserInfo, #siteRules, #detailsDescription, #usrInfoWrapper, .msgContent, .msgReplyForm > textarea, #jokeViewAddText, .jokesCommentMiddle, #quickReplyBox, #search, #searchBtn {
    background-color: #0F0F0F !important;
}

#usrInfoWrapper {
    background-color: #0F0F0F;
    box-shadow: none;
    margin-left: 10px;
    width: 760px;
    min-height: 400px;
    margin-top: 15px;
    padding: 20px;
}

.userSettingsDiv {
    width: 600px;
    border-bottom: none;
    display: inline-block;
    padding-top: 10px;
    padding-bottom: 10px;
}

#usrDetailsInfobar {
    height: 60px;
    background-color: #0f0f0f;
    color: #CCC;
    font-size: 9pt;
}

#usrDetailsAvaWrapper {
    display: table-cell;
    padding: 0;
    width: 200px;
    background: none;
    text-align: center;
}

#pagerHR, hr {
    border: none;
    color: #666666;
}

#usrDetailsAvatar {
    max-width: 160px;
    max-height: 230px;
    margin-top: 10px;
    border: 0px solid white;
    box-shadow: none;
    margin-bottom: 25px;
    border-image: linear-gradient(#fff, LightGray) 14% stretch;
    background-color: #0F0F0F;
}

.dl_Btn {
    height: 45px;
    line-height: 45px;
    border-radius: 3px;
    box-shadow: none;
    background: #008860 linear-gradient(rgba(0,0,0,0.1), RGBA(255, 255, 255, 0));
    font-weight: 700;
    text-shadow: none;
    text-decoration: none;
    transition: all .3s;
    color: #EEE;
    text-align: center;
    box-sizing: border-box;
    display: inline-block;
}

.dl_Btn:hover {
    background-color: #008860;
    color: #FFF;
    z-index: 5;
    text-decoration: none;
    box-shadow: none;
}

.Dark .sect {
    background-color: #0F0F0F;
}

.buttons {
    display: table;
    table-layout: fixed;
    width: 100%;
    background: #0F0F0F;
    color: #CCC;
    border-collapse: collapse;
    text-align: center;
}

.buttons a {
    display: table-cell;
    padding: 10px;
    border-right: 3px solid #131313;
    transition: background .3s;
    text-decoration: none;
    border-top: none;
    border-left: none;
    border-bottom:none;
}

.buttons a:hover {
    background: #151515;
}

.buttons a.tnx:before {
    content: attr(data-count);
    background: #202020;
    display: inline-block;
    padding: 0 5px;
    border-radius: 3px;
    margin-right: 5px;
}

.Dark .sect .tTitle, .Dark .sect .pagger {
    border: none;
    box-shadow: none;
}

#torrentTable td {
    background-color: #0F0F0F;
    padding: 0px 5px;
}

cloudflare-app[app="back-to-top-button"] {
    background: #101010 !important;
    border-radius: 5px !important;
    cursor: pointer;
    display: block;
    font-family: sans-serif;
    font-size: 40px;
    font-style: normal;
    font-weight: normal;
    height: 75px;
    letter-spacing: 0;
    line-height: 75px;
    position: fixed;
    text-align: center;
    text-rendering: optimizeLegibility;
    transition: opacity .3s ease-in-out;
    user-select: none;
    width: 75px;
    z-index: 7000;
}

  ::-webkit-scrollbar-track
  {
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3);
    background-color: #252525;
    border-radius: 5px;
  }

  ::-webkit-scrollbar-thumb
  {
    background-color: #1888;
    border-radius: 5px;
    background-image: -webkit-linear-gradient(90deg, rgba(0, 0, 0, .2) 25%, transparent 25%, transparent 50%, rgba(0, 0, 0, .2) 50%, rgba(0, 0, 0, 0.2) 75%, transparent 75%, transparent);
  }

  ::-webkit-scrollbar
  {
    width: 15px;
    background-color: #252525;
  }
}
