style.css:
@-moz-document domain("komplett.no") {
/*Fargevariabler*/
:root {
   --gray: #42454a;
   --lightGray: #4b4c4f;
   --darkGray: #2e2f33;
   --lightGrayHover: #d4d5d9;
   --darkGrayHover: #5b5f66;
}

/*Scrollbar*/
body::-webkit-scrollbar {
    width: 11px;
}
body::-webkit-scrollbar-track {
    background: #2a2d2f;
}
body::-webkit-scrollbar-thumb {
    background: #404448;
    border-radius: 5.5px;
}
body::-webkit-scrollbar-thumb:hover {
    background: #4b5155;
}

/*Bakgrunn på kropp*/
body, .caas {
    background-color: var(--darkGray)
}

/*Top header*/
.search-container .search-input input,.icon-button__label  {
    background-color: var(--gray);
    color: white !important;
}
.navigation-header .caas__header, .search-container .search-input input,.icon-button__label  {
    background-color: var(--gray);
}

/*Søkefelt og resultater*/
.search-container .search-input:focus-within, .search-container .search-input input:focus{
    background-color: #555960;
}
.search-container .search-input input::placeholder, product__name {
    color: white;
}
.search-container .search-input, .search-container .search-input input{
    transition: background-color .2s;
}
.search-result__container .search-result--show {
    background: var(--gray)
}
.search-result__container .search-result--show .product__title, .search-result__container .search-result--show .product__description, .search-result__container .search-result--show .product__prices, .search-result__products .search-result__category-title{
    color: white;
}

/*Nav bar bakgrunn*/
.navigation-header .caas__header .header__wrapper--bottom, .menu-navigation__root a {
    background-color: var(--lightGray);
    color: white !important;
}
/*Hover på navbar blokker*/
.menu-navigation__cell--top.menu-navigation__cell--active, .menu-navigation__cell--top:hover {
    background: var(--darkGrayHover);
}
/*Bakgrunn bak dropdown meny blokker hvis blokker ikke fyller radene nedover*/
.menu-navigation__level {
    background: var(--lightGray);
}
/*Hover på dropdpwn meny blokker*/
.menu-navigation__level-wrapper .menu-navigation__cell:hover, .menu-navigation__level-wrapper .menu-navigation__cell--active {
    background: var(--lightGrayHover);
    color: black !important;
}
/*Fjerner kant på dropdown meny*/
.menu-navigation__level {
    border: none;
}
/*Overganger*/
.menu-navigation__level-wrapper .menu-navigation__cell--active, .menu-navigation__level-wrapper .menu-navigation__cell, .menu-navigation__cell--top.menu-navigation__cell--active, .menu-navigation__cell--top {
    transition: 
        background .2s,
        color .2s;
}

/*Bakgrunn bak main content*/
#MainContent, .caas__footer, .caas__header {
    background-color: var(--darkGray);
}
/*Footer bakgrunn*/
.caas__footer--content .main, .caas__footer--content .bottom {
    background-color: var(--gray);
}
/*Tekst farge i footer*/
.caas__footer--content .main__container h3, .caas__footer--content .main__container ul li a{
    color: white !important;
}
.caas__footer--content .main__container ul li a:hover{
    text-decoration: underline;
}
/*Kant over sosiale linker i footer*/
.caas__footer--content .bottom {
   border-top: 1px solid #ccc;
}

/*Chat knapp*/
.caas .chat-wrapper .chat-header {
    background: var(--lightGray);
    color: white !important;
}
.caas .chat-wrapper .chat-header-big-text {
    color: white;
}

/*Div forbedringer*/
.caas .product-images .medium-image-carousel .swiper-slide-button:focus, .caas .box .product-list-item .image-container:focus, .caas .dropdown-btn select.background-transition:focus, .caas .recommendations-content .image-container .image:focus, .caas a:focus, .caas .product-main-info .product-main-info-buy-button .buy-button .btn-large:focus{
    outline: none;
}
}
