style.css:
@-moz-document domain(test2-scs.mp-objects.com) {
    body#loginWindow div#content {
        background: #2c6487;
    }

    div.envContainer {
        background: #2c6487;
        height: 100px;
        color: white;
    }

    h1, h2 {
        background-color: #307c9c;
        color: white;
    }

    div.tabpanel div.tab-row, tr.even button.small, tr.list1 button.small, tr.even a.small, tr.odd button.small, tr.list0 button.small, tr.odd a.small {
        background: transparant; 
    }

    table.list th, table.listStandard th, table.grid tr.headers th, table.grid.headersGrid tbody tr.headers {
        background-color: #1e4d62;
    }  

    .list1, .even, div.gridContainer table.grid tbody tr.even td:first-child {
        background-color: #c4e1ed;
    }  
}


