@-moz-document domain(XXXTESTXXX.mp-objects.com) {
    div#pageContent {
        position: relative;
    }

    div#veil {
        display: block;
        top: 100px;
        position: relative;
    }

    div#invoiceactions {
        top: 71px !important;
    }

    .dropdown .dropdown-menu li {
        list-style: none;
        margin: 0;
        line-height: 18px;
        border-bottom: 1px solid #c0c0c0;
        padding: 5px 0 5px 0px;
    }

    div#invoiceButtonSet {
        position: absolute;
        top: -100px;
        display: block;
    }

}

@-moz-document domain(XXXTESTXXX.mp-objects.com) {
    body#loginWindow div#content {
        background: #006650;
    }

    body#loginWindow div#header div.logoHeader.default {
        background-image: url('https://www.interstreambarging.com/wp-content/uploads/2017/10/logo-interstreambarging-300x76-300x76.jpg');
    }

    #menuContainer, ul.xMenu, ul.xMenu li {
        background-color: #006650;
    }

    ul.xMenu li > a.rootMenu.linkMenuItem:hover{
        color: #006650;
        background-color: white;
    }

    ul.xMenu a.rootMenu:hover,ul.xMenu a.rootMenu:visited:hover {
        color: #006650;
        background-color: white;
    }

    ul.xMenu li ul li a.subMenu>ul:focus, a.subMenu {
        background-image: url('https://scs.mp-objects.com/isbscs/lookandfeel//images/menu_submenu_hover.png');
    }

    ul.xMenu ul {
        background-color: white;
        box-shadow: 0 8px 15px rgba(0, 0, 0, 0.1);
    }

    ul.xMenu li ul li, ul.xMenu ul ul li { 
        background-color: white;
        color: #006650;
    }

    a.subMenu, a.subMenu:visited, a.subMenu:active, a.menuItem, a.menuItem:visited, a.menuItem:active {
        color: #006650;
    }

    h1, h2 {
        border-top-color: #3e3e3e;
        background-color: #027F6E;
        color: white;
    }

    table.list th, table.listStandard th, table.grid tr.headers th, table.grid.headersGrid tbody tr.headers {
        background-color: #006650;
    }  
}
