style.css:
@-moz-document domain(golangnews.com) {
body {
    background-color: #586e75;
}
header {
    background-color: #839496;
    color: #eee8d5;
}
header a {
    background-color: #839496;
    color: #eee8d5;
}
footer {
    background-color: #839496;
    color: #eee8d5;
}
footer a {
    background-color: #839496;
    color: #eee8d5;
}
li.story .bubble {
    background-color: #cb4b16;
}
li.story .bubble .bubble-tick {
    border-color: #cb4b16 transparent transparent transparent;
}
.story .metadata .date,
.story .metadata .user {
    color: #cb4b16;
}
.story .name {
    color: #fdf6e3;
}
.story .name:visited {
    color: #93a1a1;
}
li.story:hover {
    background-color: #2aa198;
}
}
