}
ytd-alert-with-button-renderer {
    background: #161616 !important;
}
.ytd-subscription-notification-toggle-button-renderer {
    background: #363636 !important;
    border-radius: 14px !important;
}
#show-hide-button {
    margin-top: 25px!important;
}
#input-panel.yt-live-chat-renderer::after {
    display: none!important;
}
ytd-searchbox[has-focus] #container.ytd-searchbox {
    border: #ff4747 solid 1px;
}
ytd-searchbox #container.ytd-searchbox {
    transition: 150ms ease-in-out;
}
.sbdd_b, .sbsb_a, .sbfl_b {
    background: #282828;
    border: none;
    border-radius: 14px;
}

.gsfs, .sbfl_a {
