}
.ytcp-paper-checkbox {
    border-radius: 5px!important;
}
.divider, .ytcp-video-row, .ytcp-primary-action-bar-0, .ytcp-table-header-0, .ytcp-video-visibility-edit-popup, .ytcp-playlist-dialog, .ytcp-primary-action-bar-2, .ytca-course-card, .ytca-separator-bottom {
