}
/*Rounded Youtube Studio End*/

/*Rounded Youtube Music*/
.ytmusic-two-row-item-renderer, .ytmusic-carousel, .ytmusic-cropped-square-thumbnail-renderer img, ytmusic-toggle-button-renderer, .ytmusic-nav-bar{
    border-radius: 14px!important;
}
/*Rounded Youtube Music End*/






#search-icon-legacy {
    display: none!important;
}
}

