style.css:
@-moz-document regexp("https?://www.google.(com|([a-z]{2}))(.[a-z]{2})?."), regexp("https?://www.google.(com|([a-z]{2}))(.[a-z]{2})?/((\\?|webhp|search|gfe_rd|auth|gws_rd|#q|imghp|#gfe_rd|#safe|#pws|#tbs|#gws|#tbm).*)"), regexp("https?://encrypted.google.(com|([a-z]{2}))(.[a-z]{2})?/((\\?|webhp|search|gfe_rd|auth|gws_rd|#q|imghp|#gfe_rd|#safe|#pws|#tbs|#gws|#tbm).*)"), regexp("https?://encrypted.google.(com|([a-z]{2}))(.[a-z]{2})?.")
  {
 body, html {
       background-color: #212121!important;
       background-image: url("https://raw.githubusercontent.com/AdityaRawat96/Images/master/dj_headphones_cap_122217_3840x2160.jpg")!important;
       background-size:cover!important;
       background-repeat:no-repeat!important;
}
a {
       text-decoration: none!important;
}
/* Searchbox container div */
 .RNNXgb {
       border:1.5px solid #fff;
       background:hsla(0, 0%, 0%, 0.3)!important;
       border-radius:10px!important;
       transform: scale(1)!important;
       transition: all ease-in-out 0.2s!important;
}
.RNNXgb:hover {
       border:1.5px solid #4285F4;
       background:hsla(0, 0%, 0%, 0.6)!important;
       border-radius:10px!important;
       transform: scale(1.01)!important;
       transition: all ease-in-out 0.2s!important;
}
/* Search box input tag */
 .gsfi {
       color:white!important;
}
/* Search and I'm feeling lucky button */
 input[type="submit"] {
       color:#fff!important;
       background:hsla(0, 0%, 0%, 0.3)!important;
       border:1.5px solid #fff!important;
       border-radius:5px!important;
       transform: scale(1)!important;
       transition: all ease-in-out 0.2s!important;
}
input[type="submit"]:hover {
       color:#4285F4!important;
       background:hsla(0, 0%, 0%, 0.6)!important;
       border:1.5px solid #4285F4!important;
       border-radius:5px!important;
       transform: scale(1.05)!important;
       transition: all ease-in-out 0.2s!important;
}
/* gmail and images link */
 .gb_d {
       color:#fff!important;
       transform: scale(1)!important;
       transition: all ease-in-out 0.3s!important;
}
.gb_d:hover {
       color:#4285F4!important;
       transform: scale(1.05)!important;
       transition: all ease-in-out 0.3s!important;
}
/* account icon */
 span.gb_ya.gbii {
       border:2px solid #fff!important;
       transform: scale(1.0)!important;
       transition: all ease-in-out 0.3s!important;
}
span.gb_ya.gbii:hover {
       border:2px solid #4285F4!important;
       transform: scale(1.05)!important;
       transition: all ease-in-out 0.3s!important;
}
/* search suggestion div */
 .aajZCb {
       transform: scale(0.96)!important;
       margin-top:2px!important;
       border-radius:5px!important;
       background:#2E2E2E!important;
}
/* all search suggestions */
 .sbct {
       background:hsla(0, 0%, 0%, 0)!important;
       transform: scale(1.0)!important;
       transition: all ease-in-out 0.3s!important;
}
.sbct:hover {
       border-radius:5px!important;
       background:#212121!important;
       transform: scale(1.03)!important;
       transition: all ease-in-out 0.3s!important;
}
/* Related searches */
 .sbl1 {
       color:#fff!important;
       font-weight:bold!important;
}
/* Recent searches */
 .sbl1p {
       color:#4285F4!important;
       font-weight:bold!important;
}
/* All unnessesary bars hidden */

/* language change bar */
 #SIvCob {
       background:hsla(0, 0%, 0%, 0)!important;
}
#SIvCob {
       color:#4285F4!important;
       font-weight:bold;
}
/* location bar */
 .b0KoTc {
       display:none!important;
}
/* Setting bar */
 .b2hzT {
       background:hsla(0, 0%, 0%, 0)!important;
       border:0px!important;
}
.hRvfYe {
       background:hsla(0, 0%, 0%, 0)!important;
       border:0px!important;
}
.Fx4vi {
       color:white!important;
}
.appbar {
       background-color:hsla(0, 0%, 0%, 0)!important;
}
#hdtbSum {
       background-color:hsla(0, 0%, 0%, 0)!important;
}
.notl {
       border:0px!important;
}
.mSAqxd {
       background-color:hsla(0, 0%, 0%, 0)!important;
       border:0px!important;
}
}
