style.css:
/* i really want this to be global */
.custom-reply {padding-top:10px!important;}
.lia-message-view-display {padding-bottom:0px!important;}
.lia-quilt-row-message-controls {display:none!important;}
.lia-quilt-row-message-quick-reply {display:none!important;}
.lia-quilt-column-message-header-right
{
    float: right !important;
    position: absolute !important;
    width: 100% !important;
}

.lia-quilt-row-message-body
{
background-color: #f5f6f1!important;
padding: 20px!important;
margin-left: 90px !important;
}

.lia-quilt-row-message-post-times
{
float: left!important;
padding: 0px !important;
margin: 0px !important;
margin-bottom: -80px !important;
margin-top: 10px !important;
margin-left: 90px !important;
position: absolute !important;
z-index:10 !important;
}

.lia-component-post-date
{
display: inline-flex !important;
}

.lia-quilt-column-message-post-times-content *
{
font-size: 10px!important;
}

.lia-quilt-column-message-post-times-content
{
margin-left:20px!important;
}

.lia-quilt-row-message-header
{
float: left!important;
}

.lia-user-avatar-messagex, .lia-component-author-avatar img
{
width: 70px!important;
height: 70px!important;
border-radius:0%!important;
}

.lia-quilt-column-message-header-left
{
padding:0px!important;
    float:left!important;
    position: absolute!important;
}

.lia-message-author-with-avatar
{
    display:none!important;
}

.lia-message-body-content
{
margin-bottom: 0px!important;
margin-top: 20px!important;
font-size: 13px!important;
}

.custom-reply
{
padding-top:0px!important;
border-top:0px!important;
}

.lia-linear-display-message-view
{
margin-top:20px!important;
}
.lia-message-body-content p, .lia-message-body p, .lia-message-editor p
{
margin-bottom:0px!important;
}

.lia-quilt-row-message-subject
{
    xmargin-bottom: -15px!important;
    float: left;
    margin-top: -73px;
    width: 100%!important;
}

.lia-quilt-column-message-subject-content
{
background-color: #eaebe5;
    padding: 10px 10px 15px 15px !important;
    xwidth: unset!important;
    xmargin-left: -20px!important;
    margin-top: 0px!important;
    display: flex!important;
}

.lia-quilt-column-breadcrumb
{
    margin-bottom:20px!important;
}

.lia-accepted-solution
{
    padding:20px!important;
}


/* ======================================== */

.lia-list-wide
{
    background-color: #f8f8f6 !important;
}


.lia-list-wide tr
{
border-top: 1px solid #e0e0df!important;
border-bottom: 1px solid #e0e0df!important;
}


.lia-data-cell-icon .lia-user-avatar-message
{
    width: 40px!important;
    height: 40px!important;
    border-radius: 3px!important;
}


.MessageSubjectCell
{
    line-height: 1px!important;
}

.MessageSubject h2 a
{
    font-size: 14px!important;
    margin: 0px!important;
}

.lia-info-area
{
    margin: 0px!important;
    padding: 0px!important;
    color: #666 !important;
    margin-top: -8px!important;
}

.lia-info-area *
{
    font-size: 11px!important;
}

.lia-dot-separator
{

}

.lia-component-messages-column-message-kudos-count
{
display: none!important;
}

.lia-component-messages-column-message-replies-count i
{
    padding: 0px!important;
    margin: 0px!important;
    margin-right: -5px!important;
}

i.lia-fa-comment-o::before
{
background-size: 15px!important;
opacity: 0.2!important;
vertical-align: -webkit-baseline-middle!important;
}

a.lia-link-navigation
{
    font-size:12px!important;
}

.lia-data-cell-text
{
    padding:5px!important;
}

.lia-panel
{
    padding:20px!important;
}

.lia-group-description
{
font-size:12px;
}

.lia-quilt-column-side-content
{
    float: left;
    padding-left: 0px!important;
    padding-right: 20px!important;  
}

.lia-quilt-column-main-content
{
    float: right;
}

.MessageSubject .lia-list-standard-inline .lia-link-navigation
{
    padding: 5px!important;
    background-color: #DDD;
    padding-top: 2px!important;
    padding-bottom: 2px!important;
    font-size: 10px!important;
    text-decoration:none!important;
}

.MessageSubject .lia-list-standard-inline .lia-link-navigation:hover
{
    background-color: #444;
    color:#FFF;
}

.lia-quilt-column-08
{
    width:25%;
}

.lia-quilt-column-16
{
    width:75%;
}

td.kudosCountColumn
{
 display:none;   
}

.cMessageAuthorAvatarColumn
{
    padding-left:15px!important;
}

.lia-data-cell-text .lia-link-navigation
{
    font-size:14px!important;
    font-weight:500!important;
}

.lia-component-message-view-widget-tags
{
    display:none!important;
}

.lia-quilt-row-message-body:before
{
    color: #f5f6f1 !important;
    content: "\f0d9" !important;
    display: block !important;
    font: normal normal normal 16px/1 FontAwesome !important;
    font-size: 32px !important;
    margin-left: -30px !important;
    -moz-osx-font-smoothing: grayscale !important;
    -webkit-font-smoothing: antialiased !important;
    text-rendering: auto !important;
    transform: translate(0, 0) !important;
    float: left;
    margin-top: -10px;
}

.lia-inline-message-reply-wrapper div.lia-placeholder-wrapper, .lia-form-board-entry
{
display:none!important;
}

.lia-inline-message-reply-wrapper div.lia-form-wrapper
{
display:unset!important;
}

.author-by
{
display:none!important;
}

.lia-message-body-content img
{
width:160px !important;
}

#lia-body.MyGroupsPage .groupName .lia-group-description::before {
background: linear-gradient(transparent 25px, #f8f8f6);
}

span.promote-banner img
{
    width:unset !important;
}

/* Hides Sticky Threads - Delete everything below this line if you prefer to view sticky threads! */
tr.lia-list-row-float
{
    display:none!important;
}

/* Hides Translate secion */
.lia-translate-message
{
    display:none!important;
}
