style.css:
@-moz-document domain(kanbanflow.com) {
/* task colors */
    /* blue */[class$="-blue"],.addTaskDialog-wrapper .taskColor--blue,.taskDetails-wrapper .taskColor--blue {background-color:
    #b0ccec !important; }
    /* green */[class$="-green"],.addTaskDialog-wrapper .taskColor--green,.taskDetails-wrapper .taskColor--green {    background-color: 
    #b8ccc2 !important; }
    /* yellow */[class$="-yellow"],.addTaskDialog-wrapper .taskColor--yellow,.taskDetails-wrapper .taskColor--yellow {    background-color: 
    #edf310 !important;}
    /* red */[class$="-red"],.addTaskDialog-wrapper .taskColor--red,.taskDetails-wrapper .taskColor--red {    background-color: 
    #ff5d5d !important;}
    /* orange */[class$="-orange"],.addTaskDialog-wrapper .taskColor--orange,.taskDetails-wrapper .taskColor--orange {    background-color: 
    #ff8725 !important; }
    /* purple */[class$="-purple"],.addTaskDialog-wrapper .taskColor--purple,.taskDetails-wrapper .taskColor--purple {    background-color: 
	#795e75 !important; color:white;}
    /* magenta */[class$="-magenta"],.addTaskDialog-wrapper .taskColor--magenta,.taskDetails-wrapper .taskColor--magenta {    background-color: 
    #fc54a7 !important; color:white;}
    /* cyan */[class$="-cyan"],.addTaskDialog-wrapper .taskColor--cyan,.taskDetails-wrapper .taskColor--cyan {    background-color: 
	#c3fff8 !important;}
    /* brown */[class$="-brown"],.addTaskDialog-wrapper .taskColor--brown,.taskDetails-wrapper .taskColor--brown {    background-color: 
    #442e1b !important; color:white;}
    /* white */#board-wrapper [class$="-white"],.addTaskDialog-wrapper .taskColor--white,.taskDetails-wrapper .taskColor--white {    background-color: 
    #f1f1f1 !important;}

.subTasks ,.task-description {
    border-top-color: #ffffff85 !important;}
.task-dueDates{
    border-top-color: #00000085 !important;}
[class^="taskDetails-"]{color: inherit;}
.taskDetails-addSubTaskWrapper{color:#333;}
.boardList-currentBoard{    color: #666;}
.boardList-currentBoard:hover{    color: #fff;}

/* system colors */
.board-background,
.board-table,
.columnHeader,
#board-wrapper,
#header {
    background-color: 
    #efefef;
}
#header:hover{
     background-color: #333;}
.columnHeader-inner {
    background-color: 
    #ffffff63;
	border: 1px solid #bdbdbd !important;
}
.columnHeader-addTask:after,
.columnHeader-addTask:before {
    background: 
    #4d4d4d;
}
.taskList,
.column-first,
.board-taskListCell {
	border: 0px solid #c3c3c3;
}
.task {
	border:0;
	box-shadow: 0 1px 0 rgba(9, 45, 66, .25);
}

.boardHeader-boardName

:hover {
    color: #242424;
}

/* scrollbar */
/*スクロールバー全体*/
::-webkit-scrollbar {
	width: 0.6em;
}
/*スクロールバーの動く部分*/
::-webkit-scrollbar-thumb {
    background-color: 
    #00003212;
	border-radius: 10px;
}



/* layout */

.columnHeader {border: 0;}
.columnHeader-inner {
	padding: 1em 1em;
	width: 98%;
	margin: 0.5em auto;
	border-radius: 3px;
    background-color: initial;
    border: 0px solid #bdbdbd !important;
}
h2.columnHeader-name{width:100%;}
.task {
	font-size: 14px;
	letter-spacing: 0.02em;
	padding: 1em 1.3em;
}
.task-name {
	font-weight: bold;
	letter-spacing: 0.03em;
}
.task-description {
    font-size: 0.85em;
}
.taskDetails-main {
	border-radius: 8px;
}
.subTasks {
    font-size: 0.9em;
}
.boardHeader-boardName {
	letter-spacing: 0.03em;
	font-weight: normal;
}
.boardHeader-boardName:hover{
	text-decoration:underline;
}
.task-avatar {border: 0}


.columnHeader-inner
,th,#header{
transition: all 300ms linear;
}
td{
transition: background 300ms linear;
}
.board-taskListCell.column-collapsed:hover 
, .columnHeader.column-collapsed *
{
    background-color: #c4c4c4;
    border-radius: 3px;
    color: white;
}
.columnHeader-count:hover{
	color:#333;
	background:transparent;
}
.collapsed-info-title{
    font-size: 1em;
    letter-spacing: 0.2em;
    color: inherit;
}

.board-taskListCell.column-collapsed{
	background-color:initial;
}
.columnHeader-count{
	font-size:1.5em;
    background-color: #00000091;
    border-radius: 3px;
	line-height:1.3em;
	height:1.5em;
	align-content:center;
	transition: all 300ms linear;
    color: white;
}
.column-collapsed {
	width:3em;
	padding: 0 1px;
}

}
