/* ==UserStyle==
@name Sourcehut Dark Mode
@namespace hg.sr.ht/~pmc/srht-dark
@version 0.1.9
@description An unofficial dark mode for Sourcehut
@author Piper McCorkle <piperswe@gmail.com>
@homepageURL https://hg.sr.ht/~pmc/srht-dark
@supportURL https://lists.sr.ht/~pmc/srht-dark
@updateURL https://hg.sr.ht/~pmc/srht-dark/raw/default/srht-dark.user.css
@license MIT
==/UserStyle== */

@-moz-document domain("sr.ht") {
  body {
    background-color: var(--gray-dark);
    color: var(--light);
  }

  .header-extension, .event-list .event, .nav-tabs .nav-link.active,
    .nav-tabs .nav-link.active:hover, code, pre, input[type=email],
    input[type=password], input[type=text], textarea.form-control,
    input[type=email]:active, input[type=email]:focus,
    input[type=password]:active, input[type=password]:focus,
    input[type=text]:active, input[type=text]:focus,
    textarea.form-control:active, textarea.form-control:focus {
      background: #2a2a2a;
      color: var(--light);
  }

  .btn.btn-default {
    color: #e9ecef;
    border-color: #e9ecef;
    background: #2a2a2a;
  }

  .btn.btn-default:hover {
    background-color: #1a1a1a;
  }
  
  .form-control:disabled, .form-control[readonly] {
    background: #4a4a4a;
    color: var(--gray);
  }

  .header-tabbed, .nav-tabs {
    border-bottom-color: #2a2a2a;
  }

  .event-list .event pre {
    color: var(--light);
  }

  .tree-list .name.blob a, .tree-list .name.file a {
    color: var(--light);
  }

  .navbar-light .navbar-nav .nav-link {
    color: rgba(255, 255, 255, .5);
  }

  .navbar-light .navbar-nav .active>.nav-link,
    .navbar-light .navbar-nav .nav-link.active,
    .navbar-light .navbar-nav .nav-link.show,
    .navbar-light .navbar-nav .show>.nav-link,
    .navbar-light .navbar-brand, .navbar-light .navbar-text a,
    .navbar-light .navbar-text, .navbar-light .navbar-brand:focus,
    .navbar-light .navbar-brand:hover {
      color: rgba(255, 255, 255, 9);
  }

  .navbar-light .navbar-nav .nav-link:hover {
    color: #fff;
  }

  .nav-tabs .nav-link {
    color: #aaa;
  }

  .text-muted, h3 small, h4 small {
    color: #878f97!important;
  }

  .tree-list .commit, .tree-list .commit a, .tree-list .date,
    .tree-list .mode, .tree-list .size {
      color: #66707a;
  }

  .code-view .lines {
    background: #3b424a;
  }

