style.css:
/* Base setting */
#titlebar-content *,  /* "Personal Titlebar" Add-on */
#navigator-toolbox *, /* FF & SM */
#navigation-toolbox * /* TB */
  { font-size: x-small !important; }

#titlebar-content *,
#toolbar-menubar *
{
  -moz-margin-start:  0px !important;
  -moz-margin-end:    0px !important;
  -moz-padding-start: 0px !important;
  -moz-padding-end:   0px !important;
}

/* Dropmarker width adjustment */
#titlebar-content .dropmarker-icon,
#toolbar-menubar .dropmarker-icon
  { min-width:10px !important; }

/* Menu */
#main-menubar menu, /* FF & SM */
#mail-menubar menu  /* TB */
{
  -moz-margin-start:  1px !important;
  -moz-margin-end:    1px !important;
  -moz-padding-start: 1px !important;
  -moz-padding-end:   1px !important;
}

/* i really want this to be global */
