style.css:
@-moz-document domain("userstyles.org") {
body {
    color: #CCC;
    font-family: 'Roboto', sans-serif;
    background-color: #0a0a0a;
    margin: 0;
    width: 100%;
}

#view-responsive {
    background-color: #0A0A0A;
}

#style_installed_button {
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    border-radius: 4px;
    background-color: #0F0F0F;
    border: none;
    width: 150px;
    height: 48px;
    font-size: 14px;
    font-weight: 600;
    color: #2ecc71;
    margin-left: 11px;
    cursor: pointer;
}

#header {
    display: flex;
    align-items: center;
    height: 68px;
    background-color: #101010;
    box-shadow: none;
    justify-content: space-between;
    position: fixed;
    top: 0;
    width: 100%;
    z-index: 5;
}

.header-search .type_search {
    align-items: center;
    height: 100%;
    border-top-left-radius: 6px;
    border-bottom-left-radius: 6px;
    background-color: #151515;
    display: flex;
    position: relative;
    cursor: pointer;
}

.header-search .search_input {
    width: 100%;
    max-width: 466px;
    height: 100%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    position: relative;
    border-top-right-radius: 6px;
    border-bottom-right-radius: 6px;
    background-color: #151515;
}

.header-search .search_input .input_text_search {
    width: 100%;
    height: 100%;
    font-size: 14px;
    color: #fff;
    background-color: #151515;
    border: none;
    padding-bottom: 2px;
    padding-left: 16px;
}

#create_new_style {
    margin-right: 23px;
    font-size: 14px;
    cursor: pointer;
    min-height: 44px;
    border-radius: 4px;
    border: solid 1px #151515;
    display: flex;
    flex-direction: column;
    text-align: center;
    justify-content: center;
    color: #E0245E !important;
    padding: 0 16px;
    white-space: nowrap;
}

#create_new_style:hover {
    border: solid 1px #151515;
    color: #E0245E;
}

#top_android_button {
    border-radius: 4px;
    background-color: #191919;
    box-shadow: none;
    display: flex;
    flex-direction: row;
    justify-content: space-evenly;
    align-items: center;
    cursor: pointer;
    padding: 10px 16px;
}

#top_android_button:hover {
    background-color: #191919;
    box-shadow: none;
}

#view-responsive {
    background: #0A0A0A;
    min-width: 728px;
}

.us-stylecard--short {
    background-color: #0F0F0F;
    border-radius: 4px;
    box-shadow: none;
    box-sizing: border-box;
    display: inline-block;
    height: 294px !important;
    margin: 10px;
    padding: 0;
    overflow: hidden;
    transition: all 0.2s ease;
    width: 280px;
}

.see_more:hover {
    background-color: #0F0F0F;
}

.see_more {
    width: 102px;
    height: 36px;
    border-radius: 4px;
    background-color: #0F0F0F;
    font-size: 14px;
    font-weight: 600;
    color: #3498db;
    margin-top: 20px;
    display: flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;
}

#footer {
    width: 100%;
    background-color: #0A0A0A;
    padding-top: 33px;
    padding-bottom: 32px;
    display: flex;
    font-size: 12px;
    z-index: 3;
    position: relative;
}

.navigation {
    background-color: #0F0F0F;
    position: fixed;
    z-index: 2;
    box-shadow: none;
    overflow-y: hidden;
    top: 0;
}

.navigation .links-box .navigation-link:hover {
    background-color: #151515;
    cursor: pointer;
}

.navigation .links-box .navigation-link {
    box-shadow: 0 -1px 0 0 #151515, 0 0 0 0 #151515;
    padding: 13px 0 12px 0;
    display: flex;
    align-items: center;
}

.navigation .links-box .navigation-link-active {
    border-left: 3px solid #E0245E;
}

.navigation .links-box .navigation-link .navigation-title {
    color: #CCC;
    margin: 0 0 0 10px;
}

#overlay-info {
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    position: absolute;
    width: 880px;
    height: 91px;
    opacity: 0.95;
    background-color: rgba(10, 10, 10, 0.98);
    border-bottom-left-radius: 4px;
    border-bottom-right-radius: 4px;
    top: 262px;
}

.statistic table, th, td {
    color: #CCC;
    border: none;
}

.white_button {
    border-radius: 4px;
    background-color: #101010;
    box-shadow: none;
    border: none;
    display: flex;
    align-items: center;
    justify-content: center;
    height: 48px;
    padding: 0px 10px;
    font-size: 14px;
    color: #CCC;
    font-weight: bold;
    cursor: pointer;
}

.white_button:hover {
    background-color: #0f0f0f;
    box-shadow: none;
}

#install_style_button {
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;
    border-radius: 4px;
    background-color: #3498db;
    box-shadow: none;
    color: white;
    font-size: 14px;
    cursor: pointer;
    font-weight: bold;
    width: 173px;
    height: 48px;
    margin-left: 9px;
}

.css_button {
    width: 171px;
    height: 43px;
    border-radius: 4px;
    background-color: #0F0F0F;
    display: flex;
    flex-direction: row;
    font-size: 14px;
    font-weight: bold;
    align-items: center;
    justify-content: center;
    color: #3498db;
    margin-top: 30px;
    cursor: pointer;
}

.css_button:hover {
    background-color: #0F0F0F;
}

#archive_style_button {
    border-radius: 4px;
    box-shadow: none;
    border: none;
    font-size: 14px;
    font-weight: 500;
    text-align: center;
    justify-content: center;
    color: #c14430;
    width: 171px;
    padding-top: 15px;
    padding-bottom: 15px;
    margin-top: 30px;
    cursor: pointer;
    background-color: #0F0F0F;
}

#archive_style_button:hover {
    border: none;
    color: #a83b2a;
}

#send_feedback_button {
    width: 172px;
    height: 48px;
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;
    border-radius: 4px;
    border: none;
    background-color: #0f0f0f;
    box-shadow: none;
    color: #2c3e50;
    font-size: 14px;
    font-weight: bold;
    cursor: pointer;
    margin-top: 10px;
    margin-bottom: 18px;
}

#send_feedback_button:hover {
    background-color: #0f0f0f;
    box-shadow: none;
}

#footer-mobile {
    width: 100%;
    background-color: #0a0a0a;
    padding-top: 25px;
    padding-bottom: 32px;
    display: flex;
    font-size: 12px;
    z-index: 1;
    position: relative;
}

.header-search .type_search .type_search_select {
    position: absolute;
    top: 0;
    width: 91px;
    border-radius: 2px;
    background-color: #0f0f0f;
    box-shadow: none;
}

.header-search .type_search .type_search_select .type_search_option {
    font-size: 14px;
    color: #CCC;
    height: 36px;
    line-height: 25px;
    padding-left: 8px;
    cursor: pointer;
    display: flex;
    align-items: center;
}

.navigation .links-box {
    box-shadow: none;
}

.errorExplanation {
    padding: .5em;
    background-color: #E0245E;
    margin-top: .5em;
    margin-bottom: .5em;
    color: #000;
    border-radius: 10px;
}

select, textarea, input, select:focus, textarea:focus, input:focus {
    border: none !important;
    background: #151515;
    font-size: 16px;
    color: #CCC;
}

 ::-webkit-scrollbar-track
  {
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3);
    background-color: #252525;
    border-radius: 5px;
  }

  ::-webkit-scrollbar-thumb
  {
    background-color: #008860;
    border-radius: 5px;
    background-image: -webkit-linear-gradient(90deg, rgba(0, 0, 0, .2) 25%, transparent 25%, transparent 50%, rgba(0, 0, 0, .2) 50%, rgba(0, 0, 0, 0.2) 75%, transparent 75%, transparent);
  }

  ::-webkit-scrollbar
  {
    width: 13px;
    background-color: #252525;
  }
}
