style.css:
@-moz-document url-prefix("https://ekstrabladet.dk/"), url-prefix("https://nationen.ebcomments.dk/"), url-prefix("https://static.frmt.dk/") {
/* Fjerner bannere */
#manipulate_apn_monster,
.eb-position--artikelbannerstor,
.editorial_takeover,
.list-ekstracontainer,
#fn_subscriptionMenu_parent, 
.bg--breaking {
    display: none !important;
}

/* Gør baggrundsfarven mørk */
body,
.masthead,
.eb-bg--white,
.topnav,
.eb-bg--graa6,
.drfront .df-container.df-skin-01_white, .drfront .df-container.df-skin-white, 
.bg--white,
.drfront .df-article.df-skin-08_white .df-article-content, .drfront .df-article.df-skin-white .df-article-content, 
.bg--graa6, .bg--graa5, .image-container--landscape {
    background-color: #1b1b1b;
}

/* Gør tekstfarven lys */
.eb-color--black,
.eb-bg--white,
.stickybackground,
.formatelm__header,
.eb-color--nyheder2,
.talk-pane,
.Tab__buttonSub___2GrIi,
.plista_widget_belowArticle2_var_2 .itemTitle,
.eb-bg--graa6,
.color--black,
.bg--white,p,h1,h2, h3, span {
	color: rgba(255, 255, 255, .5) !important;
}
a {
    color: rgba(189, 17, 24, 0.8);
}
.drfront .df-article.df-skin-25_native-dark .df-native {
    background-color: unset;
}
.medielogin-logo svg {
    fill: #ccc;
}

/* "BREAKING" farve fix */
.drfront .df-article.df-skin-01_breaking .df-article-content, .drfront .df-article.df-skin-ny-breaking .df-article-content, .drfront .df-article.df-skin-14_breaking2-ny .df-article-content, .drfront .df-article.df-skin-tour .df-article-content {
    background-color: rgba(255, 255, 0, 0.25);
}
/* Rød farve fix */
.drfront .df-article.df-skin-02_eb-ny .df-article-content, .drfront .df-article.df-skin-red .df-article-content,
.bg--red {
    background-color: rgba(189, 17, 24, 0.3);
}
/* Grøn farve fix */
.drfront .df-article.df-skin-06_sport-ny .df-article-content, .drfront .df-article.df-skin-green .df-article-content,
.bg--sport {
    background-color: rgba(50, 162, 55, 0.4);
}
/* Blå farve fix */
.drfront .df-article.df-skin-05_nyheder .df-article-content, .drfront .df-article.df-skin-bluegreen .df-article-content {
    background-color: rgba(18, 80, 123, 0.4);
}
/* Pink farve fix */
.drfront .df-article.df-skin-20_sex-samliv .df-article-content {
    background-color: rgba(138, 12, 54, 0.5);
}
/* Orange farve fix */
.drfront .df-article.df-skin-16_nationen-ny .df-article-content,
.bg--flash {
    background-color: rgba(227, 129, 33, 0.5);
}
/* Lilla farve fix */
.drfront .df-article.df-skin-09_filmmagasinet .df-article-content, .drfront .df-article.df-skin-film .df-article-content {
    background-color: rgba(101, 1, 136, 0.5);
}
/* Gør billeder mindre skrigene */
.widget-image, img {
    opacity: 0.8 !important;
}
/* Fader EB+ artikler ud */
.df-img-skin-premium {
    opacity: 0.15;
    pointer-events: none !important;
}

/* Gør border farverne mørkere */
.topnav {
    border: 1px solid #4d4d4d;
}
.drfront .df-article {
    border-top: 1px solid #4d4d4d;
}

/* Ændrer bg farven i "Det mener Ekstra Bladet */
.eb-bg--graa5 {
    background: #262626;
}

/* Fjerner unødvendig border */
a.list--flex.eb-row--itemstretch.list-item.list-item--column.eb-row-item--grow.size1of3.eb-color--black {
    border: none;
}

/* Menu bg */
.menu--overlay {
    background-color: hsla(0, 0%, 0%, 0.97);
}

/* Fakta boks gradient */
.facts-coverup--graa6 {
    background: -webkit-gradient(linear,left top,left bottom,from(hsla(0, 0%, 0%, 0)),to(#000000));
    background: linear-gradient(hsla(0, 0%, 0%, 0),#000000);
}
}
