style.css:
@-moz-document domain("userstyles.org") {
body:after{
    content: 'Урезанный стиль UserStyles из расширения "Чёрный Интернет"'!important;
    position: fixed!important;
    border: 1px solid #333!important;
    color: #FFF!important;
    background: #333!important;
    left: 0px!important;
    bottom: 0px!important;
    z-index: 999!important;
    padding: 0 3px!important;
}
*, ::placeholder {
	color: #fff!important;
}
::selection {
	background: #fff!important;
	color: #000!important;
}
#as_userscript a, #as_userscript a:hover, #as_userscript a:visited, #as_userscript a:visited:hover, *:not(.notes_textarea):not(.text_style_page):not(.style_description_long):not(.us-username):not(.NotificationLine-Content)>a, .author-name a, #discussion_author, #discussion_author a, #discussion_author a:visited, #discussion_author a:visited:hover {
	-webkit-text-fill-color: #fff!important;
}
.overlay_background, .android_button_banner, #top_android_button, .android_button_button, .walking, .NotificationLine, #footer, [src="/ui/images/pieLoader.gif"], .__react_component_tooltip.type-dark.place-top:after {
	display: none!important;
}
#nav-view-responsive, .header-search .type_search .type_search_select, div#installStyleButton, textarea, .CodeMirror, .CodeMirror-gutters, .white_button, #update_style_button, .choose_editor .boxesContainer .box, .us-stylecard__button-container .us-button, .sc-bdVaJa.fzKkUK, .personal-page .light-blue-button, .us-stylecard--long, input, .sketch-picker, #style-settings select, .advancedsettings_shown, #install_button, .style_category_card_wrapper, #style_installed_button, .customize_button, #donation_button, #stylish-code, .div-arrow, .current, #login .btn-auth, html, body, #send_feedback_button, #notes_textarea, .css_button, #install_style_button, .see_more, #overlay-info, .header-search .type_search, .header-search .search_input, #header, .navigation, #view-responsive, .us-stylecard--short, .ckfDBv, .android-sp, .android-sp-info, .android-sp-info .download-app-button, .android-sp-info .tabbed-area-div .tabs-container, .android-sp-info .us-widget, .android-sp-info .light-blue-button {
	background: #000!important;
}
.header-search .type_search .type_search_select .type_search_option:hover, input[type="button"]:hover, #archive_style_button:hover, .white_button:hover, #install_style_button:hover, #update_style_button:hover, .us-stylecard__button-container .us-button:hover, .personal-page .light-blue-button:hover, .personal-page .light-blue-button:hover, #install_button:hover, .style_category_card_wrapper:hover, #style_installed_button:hover, .customize_button:hover, #donation_button:hover, #send_feedback_button:hover, .css_button:hover, .see_more:hover, #create_new_style:hover, .navigation .links-box .navigation-link:hover, .android-sp-info .download-app-button:hover, .android-sp-info .light-blue-button:hover {
	background: #333!important;
}
div#installStyleButton, #archive_style_button, .white_button, #update_style_button, .choose_editor .boxesContainer .box, .us-stylecard__button-container .us-button, .sc-bdVaJa.fzKkUK, .personal-page .light-blue-button, .us-stylecard--long, input, .sketch-picker, .advancedsettings_shown, #install_button, #style_installed_button, .customize_button, #donation_button, #stylish-code, .div-arrow, .current, #login .btn-auth, #send_feedback_button, .css_button, #install_style_button, .see_more, #overlay-info, #create_new_style, .header-search .type_search, .header-search .search_input, .us-stylecard--short, .statistic table, th, td, .ckfDBv, .android-sp-info .download-app-button, .android-sp-info .us-widget, .android-sp-info .light-blue-button {
	border: 1px solid #fff!important;
}
.navigation .links-box .navigation-link-active {
	border-left: 3px solid #fff!important;
}
.android-sp-info {
	border-left: 1px solid #fff!important;
}
.navigation {
	border-right: 1px solid #fff!important;
}
input.input_text_search {
	border: none!important;
}
.header-search .search_input .search_image {
	right: 2px!important;
	width: 23px!important;
	border-radius: 1px!important;
	border-right: 1px solid #fff!important;
}
#header {
	border-bottom: 1px solid #fff!important;
}
#as_userscript a, #as_userscript a:hover, #as_userscript a:visited, #as_userscript a:visited:hover {
	text-decoration: none!important;
}
.choose_editor {
	background-color: rgba(0, 0, 0, 0.67)!important;
}
#style_info {
	margin-top: 30px!important;
}
.PageContent {
	margin-top: 40px!important;
}
#nav-view-responsive {
	padding-top: 0px!important;
}
