/* All Vars Used */
:root {
	/* S-CSS-P Integration */
	/* If you're making a new CSS theme, please include the following three variables at minimum. */
	--theme-base: "nuscp";
	/* must be either "nuscp" or "sigma9" */
	--theme-id: "nuscp";
	/* set this to the URL of your theme's page - eg for "component:ar-theme", set it to "ar-theme" */
	--theme-name: "nu-SCP Theme";
	/* set this to your theme's full name */
	/* Header */
	--logo-image: url("https://nu-scptheme.github.io/Nu-SCP/images/logo.svg");
	--header-title: "SCP FOUNDATION";
	--header-subtitle: "SECURE, CONTAIN, PROTECT";

	/* Typefaces */
	--body-font: 'Lato', sans-serif;
	--header-font: 'Poppins', sans-serif;
	--title-font: 'Poppins', sans-serif;
	--mono-font: "PT Mono", "Andale Mono", "Courier New", Courier, monospace;

	/* Standard Colors */
	--white-monochrome: 252, 252, 252;
	/* white */
	--pale-gray-monochrome: 244, 244, 244;
	/* v light gray for blockquotes and stuff */
	--light-gray-monochrome: 170, 170, 170;
	/* light accent gray for login status */
	--gray-monochrome: 66, 66, 72;
	/* gray */
	--dark-gray-monochrome: 48, 48, 52;
	/* dark accent gray for sidebar background */
	--black-monochrome: 12, 12, 12;
	/* black */
	--bright-accent: 180, 35, 35;
	/* bright red */
	--medium-accent: 140, 20, 25;
	/* medium red */
	--dark-accent: 100, 3, 15;
	/* dark red */
	--newpage-color: 221, 102, 17;
	/* pale orange */
	/* Primary Theme Colors */
	--swatch-background: var(--white-monochrome);
	--swatch-primary: var(--bright-accent);
	--swatch-primary-darker: var(--medium-accent);
	--swatch-primary-darkest: var(--dark-accent);
	/* Primary Text Colors */
	--swatch-text-dark: var(--black-monochrome);
	--swatch-text-light: var(--white-monochrome);
	--swatch-important-text: var(--bright-accent);

	/* Primary Menu Colors */
	--swatch-menubg-color: var(--white-monochrome);
	--swatch-menubg-light-color: var(--pale-gray-monochrome);
	--swatch-menubg-medium-color: var(--light-gray-monochrome);
	--swatch-menubg-medium-dark-color: var(--gray-monochrome);
	--swatch-menubg-dark-color: var(--dark-gray-monochrome);
	--swatch-menubg-black-color: var(--black-monochrome);
	--swatch-menubg-hover-color: var(--black-monochrome);
	--swatch-menutxt-dark-color: var(--black-monochrome);
	--swatch-menutxt-light-color: var(--white-monochrome);
	--swatch-border-color: var(--black-monochrome);
	/* Primary Header Colors */
	--swatch-headerh1-color: var(--white-monochrome);
	--swatch-headerh2-color: var(--white-monochrome);
	--swatch-topmenu-border-color: var(--black-monochrome);
	--swatch-topmenu-bg-color: var(--gray-monochrome);

	--rating-module-button-color: var(--black-monochrome);
	--rating-module-text-color: var(--swatch-menutxt-dark-color);

	/* Header Gradients */
	--background-gradient-color: 215, 215, 215;
	--background-gradient-distance: 40rem;

	--gradient-header: linear-gradient(to top,
	rgba(99, 33, 37, 0.97) 0%,
	rgba(76, 25, 30) 70%,
	rgba(56, 37, 37) 100%);
	--gradient-topmenu: linear-gradient(to bottom,
	white 0%,
	white 7.5rem,
	rgba(var(--swatch-topmenu-border-color), 1) 7.5rem,
	rgba(var(--swatch-topmenu-bg-color), 1) 7.625rem,
	rgba(var(--swatch-topmenu-bg-color), 0.95) calc(100% - 0.125rem),
	rgba(var(--swatch-topmenu-bg-color), 1) calc(100% - 0.125rem),
	rgba(var(--swatch-topmenu-border-color), 1) 100%);
	--gradient-topmenu-mobile: linear-gradient(to bottom,
	rgba(var(--swatch-topmenu-border-color), 1) 0,
	rgba(var(--swatch-topmenu-bg-color), 1) 0.125rem,
	rgba(var(--swatch-topmenu-bg-color), 1) calc(3rem - 0.125rem),
	rgba(var(--swatch-topmenu-bg-color), 1) calc(3rem - 0.125rem),
	rgba(var(--swatch-topmenu-border-color), 1) 3rem);
	--diagonal-stripes: repeating-linear-gradient(45deg,
	hsla(0, 0%, 100%, 0),
	hsla(0, 0%, 100%, 0) 0.25vh,
	rgba(88, 88, 88, 0.1) 0.35vh,
	rgba(88, 88, 88, 0.2) 0.5vh);
	--gradient-sidemenu-header: linear-gradient(10deg,
	rgba(var(--medium-accent), 0.45) 0%,
	rgba(var(--medium-accent), 0.55) 100%);
	--gradient-background: linear-gradient(to bottom,
	rgba(var(--background-gradient-color), 1) 0,
	rgba(var(--background-gradient-color), 0.987) calc(0.008 * 40rem),
	rgba(var(--background-gradient-color), 0.951) calc(0.029 * 40rem),
	rgba(var(--background-gradient-color), 0.896) calc(0.064 * 40rem),
	rgba(var(--background-gradient-color), 0.825) calc(0.11 * 40rem),
	rgba(var(--background-gradient-color), 0.741) calc(0.166 * 40rem),
	rgba(var(--background-gradient-color), 0.648) calc(0.231 * 40rem),
	rgba(var(--background-gradient-color), 0.55) calc(0.304 * 40rem),
	rgba(var(--background-gradient-color), 0.45) calc(0.383 * 40rem),
	rgba(var(--background-gradient-color), 0.352) calc(0.467 * 40rem),
	rgba(var(--background-gradient-color), 0.259) calc(0.554 * 40rem),
	rgba(var(--background-gradient-color), 0.175) calc(0.644 * 40rem),
	rgba(var(--background-gradient-color), 0.104) calc(0.735 * 40rem),
	rgba(var(--background-gradient-color), 0.049) calc(0.825 * 40rem),
	rgba(var(--background-gradient-color), 0.013) calc(0.914 * 40rem),
	rgba(var(--background-gradient-color), 0) 40rem);
	--header-background-image-size: 100% 7.5rem;

	/* Spacing Measurements */
	--offset-from-page-top: 1.2rem;
	/* space between the top of the page and the start of the header */
	--body-width-on-desktop: 44.25rem;
	--sidebar-width-on-desktop: calc(var(--base-font-size) * 19);
	--sidebar-width-on-mobile: 14rem;
	--scrollbar-width: 0.5625rem;
	--border-styling: solid 0.0625rem rgba(var(--swatch-border-color));
	--border-radius-width: 0.0625rem;

	/* Box-Shadow 1px Borders */
	--box-border-bottom: 0 0.0625rem 0 0 rgba(var(--swatch-border-color));
	--box-border-top: 0 -0.0625rem 0 0 rgba(var(--swatch-border-color));
	--box-border-left: -0.0625rem 0 0 0 rgba(var(--swatch-border-color));
	--box-border-right: 0.0625rem 0 0 0 rgba(var(--swatch-border-color));
	--box-border-all: 0 0 0 0.0625rem rgba(var(--swatch-border-color));

	/* Sidebar */
	--sidebar-transition-timing: 0.5s ease-in-out 0.1s;
	--sidebar-internal-border-thickness: 0.125rem;

	/* final values */
	--base-font-size: 0.926rem;
	--base-line-height: 1.295rem;
}



