html {
	color: rgba(var(--swatch-text-dark));
	font-size: 1em;
	line-height: 1.5;
}

body {
	/* coloring */
	background-color: rgba(var(--swatch-background), 1);
	color: rgba(var(--swatch-text-dark));

	/* font behavior */
	font-size: var(--base-font-size);
	font-kerning: normal;
	font-variant-numeric: normal;
	-moz-font-feature-settings: "lnum", "kern", "liga", "clig", "calt";
	-ms-font-feature-settings: "lnum", "kern", "liga", "clig", "alt";
	-webkit-font-feature-settings: "lnum", "kern", "liga", "clig", "calt";
	font-feature-settings: "lnum", "kern", "liga", "clig", "calt";
	text-rendering: auto;
	-webkit-font-smoothing: antialiased;
	-moz-osx-font-smoothing: grayscale;

	/* line behavior */
	orphans: 2;
	-webkit-hyphens: manual;
	-moz-hyphens: manual;
	-ms-hyphens: manual;
	hyphens: manual;
	word-break: normal;
}

/* HTML5 Media Items */
