#side-bar ul {
	list-style-type: none;
	padding: 0 0.250rem;
}

#side-bar p {
	margin: 0;
}

/* Search box specific styling */
#___gcse_0 > div {
	display: inline-flex;
	flex-direction: row;
	width: 100%;
	height: 2rem;
}

#___gcse_0 > div > form {
	margin: 0;
}

#___gcse_0 > div > form > table > tbody > tr > td.gsc-search-button > button {
	color: rgba(var(--swatch-menutxt-light-color));
	background-color: rgba(var(--swatch-primary-darkest), 0);
	transition: background-color 80ms cubic-bezier(0.4, 0.0, 0.2, 1);
	line-height: 1.85rem;
}

#___gcse_0 > div > form > table > tbody > tr > td.gsc-search-button > button:hover,
#___gcse_0 > div > form > table > tbody > tr > td.gsc-search-button > button:focus {
	background-color: rgba(var(--swatch-menubg-color), 0.15);
	text-decoration: none;
}

/* Sigma Overwrite Specific Styles */
#side-bar > div[style="background-color: #e5e5ff;"] {
	padding: 1rem 0;
	border: 0;
	margin-top: 1rem;
	margin-bottom: 1rem;
	display: flex;
	flex-direction: column;
	flex-shrink: 0;
}

#side-bar > div[style="background-color: #e5e5ff;"] > div.menu-item > div {
	height: auto;
	display: flex;
	width: 100%;
	justify-content: space-evenly;
}

#side-bar > div[style="background-color: #e5e5ff;"] > div.menu-item > div > a {
	width: 2rem;
	margin: 0;
	padding: 0;
	border: none;
	border-radius: none;
}

#side-bar > div[style="background-color: #e5e5ff;"] > div.menu-item > div > a > img {
	display: block;
}

#side-bar .close-menu {
	display: none;
}

/* there's an empty menu item in the old sidebar design */
#side-bar div.menu-item:empty {
	display: none;
}

@media only screen and (max-width: 768px) {
	#side-bar {
		display: block;
		position: fixed;
		top: 0;
		left: -18rem;
		height: 100%;
		overflow-y: auto;
		z-index: 10;
		-webkit-transition: left var(--sidebar-transition-timing);
		-moz-transition: left var(--sidebar-transition-timing);
		-ms-transition: left var(--sidebar-transition-timing);
		-o-transition: left var(--sidebar-transition-timing);
		transition: left var(--sidebar-transition-timing);
	}

	#side-bar:after {
		content: "";
		position: absolute;
		top: 0;
		width: 0;
		height: 100%;
		background-color: rgba(var(--swatch-menubg-black-color), 0.2);
	}

	/* We've got two different mobile sidebars going on.
The first is "#side-bar:target". This is Wikidot's default.
The second is "#skrollr-body:target #side-bar". This gives us a higher level of control over what happens when the sidebar is opened.
Both need to be supported, in case certain wikis are unwilling to change the URL in nav:top. */
	/* DEFAULT SIDEBAR */
	#side-bar .close-menu {
		display: block;
		position: fixed;
		width: 100%;
		height: 100%;
		top: 0;
		left: 0;
		margin-left: 0.5625rem;
		padding-right: calc(14rem + 0.5625rem);
		background: rgba(var(--swatch-menubg-black-color), 0.3) 0.0625rem 0.0625rem repeat;
		z-index: -1;
		visibility: hidden;
		opacity: 0;
		pointer-events: none;
		transition: all var(--sidebar-transition-timing);
	}

	#side-bar:target,
	#skrollr-body:target #side-bar {
		overflow-x: hidden;
		left: 0;
	}

	#side-bar:target {
		display: block;
		left: 0;
		box-shadow: inset 0 0 0 -0.0625rem rgba(var(--swatch-menutxt-light-color));
		z-index: 10;
		margin: 0;
		width: 17em;
	}

	#side-bar:target + #main-content {
		left: 0;
	}

	#side-bar:target .close-menu {
		width: calc(100% - 14rem - 0.5625rem);
		height: 100%;
		margin-left: calc((17rem + 2 * 0.5625rem) - 0.5rem);
		padding-right: 0;
		visibility: visible;
		pointer-events: auto;
		opacity: 1;
		display: block;
		position: fixed;
		z-index: -1;
		top: 0;
		left: 0;
		background: rgba(var(--swatch-menubg-black-color), 0.3) 1px 1px repeat;
	}

	/* NEW SIDEBAR */
	#skrollr-body:target {
		overflow-x: hidden;
	}

	#skrollr-body:target #side-bar {
		display: block;
		left: 0;
		box-shadow: inset 0 0 0 -0.0625rem rgba(var(--swatch-menutxt-light-color));
		z-index: 10;
		margin: 0;
	}

	#skrollr-body:target #side-bar + #main-content {
		left: 0;
	}

	#skrollr-body:target #side-bar .close-menu {
		width: calc(100% - 14rem - 0.5625rem);
		margin-left: calc(14rem + 2 * 0.5625rem - 1px);
		visibility: visible;
		pointer-events: auto;
		opacity: 1;
		left: calc(0.5625rem * -1);
	}

	#skrollr-body:target#side-bar::-webkit-scrollbar-track {
		background: rgba(var(--swatch-menubg-color), 0.2);
	}
}

#header {
	width: 90vw;
	position: relative;
	z-index: 10;
	height: 9.375rem;
	background-image: var(--logo-image);
	background-repeat: no-repeat;
	background-position: left 0.75rem top 1.1875rem;
	background-clip: border-box;
	background-size: auto 5.375rem;
	filter: drop-shadow(.063rem .063rem .063rem rgba(var(--black-monochrome)));
}

body {
	background-image: var(--gradient-header), var(--gradient-topmenu), var(--gradient-background);
	background-size: var(--header-background-image-size), 100% 9.375rem, 100% 40rem;
	/* 120px, 150px */
	background-repeat: repeat-x;
}

div#container-wrap {
	background-image: var(--diagonal-stripes);
	background-size: 100% 7.5rem;
	background-repeat: no-repeat;
	background-position: 0 0;
}

#header,
#top-bar {
	max-width: 61.25rem;
	/* 980px */
	font-weight: 600;
	margin: 0 auto;
}

#header > h1 > a > span {
	display: inline-table;
	z-index: 9999;
	color: transparent;
	width: 50vw;
	max-width: 32rem;
	height: 7.5rem;
	position: absolute;
	left: 0;
	top: 0;
}

.mobile-top-bar {
	display: none;
	position: relative;
	left: 1rem;
	bottom: 0;
	z-index: 9999;
}

header h1 {
	font-family: var(--header-font);
	text-transform: uppercase;
	width: 100%;
	height: 100%;
	max-height: 7.75rem;
	position: absolute;
}

#header h1 a::before {
	content: var(--header-title);
	color: rgba(var(--swatch-text-light));
	text-shadow: 0.063rem 0.063rem 0.063rem rgba(var(--swatch-text-dark));
}

#header h1,
#header h2 {
	font-family: var(--header-font);
	font-weight: 700;
	position: absolute;
	-webkit-font-smoothing: antialiased;
	text-rendering: optimizeLegibility;
	-moz-osx-font-smoothing: grayscale;
}

#header h1 a {
	font-family: var(--header-font) !important;
	letter-spacing: -0.02em;
	display: block;
	line-height: 0;
	max-height: 0;
	color: rgba(var(--swatch-text-light));
	font-size: 135%;
	text-decoration: none;
	text-shadow: none;
	z-index: 9999;
	padding: 1.875rem 0 0;
	margin-left: 6.75rem;
	margin-top: calc(0.3125rem + var(--offset-from-page-top));
}

#header h2 {
	font-size: 0.945rem;
}

#header h2 span {
	display: block;
	line-height: 0;
	font-weight: 400;
	color: transparent;
	text-shadow: none;
	margin-left: 6.75rem;
	margin-top: calc(3.8rem + var(--offset-from-page-top));
}

#header h2 span::before {
	content: var(--header-subtitle);
	color: #fafafa;
	text-shadow: 0.063rem 0.063rem 0.063rem rgba(var(--swatch-text-dark));
}

#header-extra-div-1 {
	display: none;
}

#header-extra-div-2 {
	display: none;
}

#search-top-box {
	display: infline-flex;
	position: absolute;
	top: 47%;
	right: 3%;
	width: auto;
	text-align: center;
}

#search-top-box-input {
	border: var(--border-styling);
	border-radius: var(--border-radius-width);
	color: rgba(var(--swatch-text-light));
	background-color: rgba(var(--gray-monochrome)) !important;
}

#search-top-box-input:hover,
#search-top-box-input:focus {
	border: var(--border-styling);
	border-radius: var(--border-radius-width);
	color: rgba(var(--white-monochrome));
	background: rgba(var(--swatch-menubg-medium-color)) !important;
