	color: rgba(var(--swatch-menutxt-dark-color));
}

#top-bar .mobile-top-bar {
	display: none;
	position: relative;
	left: 1rem;
	bottom: 0;
	z-index: 9999;
	filter: unset;
}

#top-bar {
	width: 100%;
	position: initial;
	z-index: 50;
	height: 1.625rem;
	font-size: calc(var(--base-font-size) * 0.85);
	font-weight: 600;
	display: inline-flex;
	margin: calc(7.5rem + 0.125rem) 0 1.75rem 0;
	padding: 0 0 0.25rem;
	flex-wrap: nowrap;
}

/* In-Progress Top-Bar Implimentation */
#top-bar div.top-bar > ul,
#top-bar div.mobile-top-bar > ul {
	width: 90vw;
	flex-direction: row;
