/* ===ELEMENTS=== */
/* Modules, IMG blocks, text blocks, and other elements used outside of the structural html */
/* TABVIEW */
.yui-navset * {
	transition: background-color 80ms cubic-bezier(0.4, 0.0, 0.2, 1), color 80ms cubic-bezier(0.4, 0.0, 0.2, 1);
}

.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
	zoom: unset;
	display: flex;
	flex-wrap: wrap;
	border-color: rgba(var(--swatch-primary-darker));
	width: calc(100% - 0.125rem);
	margin: 0 auto;
	border: 0;
	box-shadow: 0 calc(0.0625rem * 5) 0 0 rgba(var(--swatch-primary-darker));
}

.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
	zoom: unset;
	background-image: none;
	background-color: rgba(var(--swatch-menubg-light-color));
	/* tab background */
	border-color: transparent !important;
	border: unset;
	color: rgba(var(--swatch-text-dark));
}

.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
	zoom: unset;
	background-color: rgba(var(--swatch-primary-darkest));
	color: rgba(var(--swatch-text-light));
}

.yui-navset .yui-nav li,
.yui-navset .yui-navset-top .yui-nav li {
	background-color: rgba(var(--swatch-menubg-light-color));
	border-color: transparent;
	max-width: 100%;
	padding: 0;
	margin: 0;
	zoom: unset;
	display: flex;
	position: relative;
	flex-basis: 7vw;
	flex-grow: 2;
	box-shadow: 0 0 0 0.0625rem rgba(var(--swatch-primary-darker));
}

.yui-navset .yui-nav li a,
.yui-navset-top .yui-nav li a,
.yui-navset-bottom .yui-nav li a {
	display: flex;
	vertical-align: bottom;
	zoom: unset;
	width: 100%;
	align-items: center;
	justify-content: center;
}

.yui-navset .yui-nav a em,
.yui-navset .yui-navset-top .yui-nav a em {
	padding: 0.35em 0.75em;
	white-space: nowrap;
	text-overflow: ellipsis;
	overflow: hidden;
}

.yui-navset .yui-nav li em {
	border: unset !important;
}

/* Selected Tab */
.yui-navset .yui-nav .selected,
.yui-navset .yui-navset-top .yui-nav .selected {
	padding: 0;
	margin: 0;
	flex-grow: 2;
	background-color: rgba(var(--swatch-primary-darker));
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a em {
	border: none !important;
	border-color: transparent;
}

.yui-navset .yui-nav .selected a {
	color: rgba(var(--white-monochrome)) !important;
	width: 100%;
	background-image: none;
}

.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:active {
	background-color: rgba(var(--swatch-primary-darker));
	color: rgba(var(--swatch-text-light));
}

.yui-navset .yui-nav .selected a:hover {
	background-color: rgba(var(--swatch-menubg-medium-color));
	color: rgba(var(--swatch-text-light));
}


.yui-navset .yui-content {
	background-color: rgba(var(--swatch-menubg-light-color), 0.25);
	/* content background color */
}

.yui-navset .yui-content,
.yui-navset .yui-navset-top .yui-content {
