/* BLOCKQUOTE */
blockquote {
	border-style: dashed;
	border-color: #a7a7a7;
	border-width: 0.063em;
	background-color: rgba(var(--swatch-menubg-light-color));
	box-shadow: 0em 0.063em 0.125em rgba(0, 0, 0, 0.1);
}

/* IMAGE BLOCK */
div.image-block {
	float: right;
	margin: 0 2em 1em 2em;
	width: 300px;
}

div.image-block img {
	width: 300px;
}

/* RATING */
:root {
	--rating-module-height: 1.3125rem;
}

/* Plain, no-credit-button rating module */
.page-rate-widget-box {
	/* position: static;
	margin-top: -3rem; */
	overflow: hidden;
	display: inline-flex;
	border-radius: 0;
	box-shadow: 0.0625rem 0.0625rem 0px rgba(var(--swatch-menubg-black-color), 0.15);
	padding: 0 0.125rem;
	margin-bottom: 0.75rem;
	/*background: linear-gradient(to top, rgba(99, 33, 37) 0%, rgba(76, 25, 30) 70%, rgba(56, 37, 37) 100%);*/
	background: linear-gradient(to top,
	rgba(var(--swatch-menubg-medium-color), 0.25) 0%,
	rgba(var(--swatch-menubg-medium-color), 0.15) 100%);
	background-color: rgba(var(--swatch-background));
	border: 0.0625rem solid rgba(var(--swatch-menubg-black-color), 0.25);
	height: var(--rating-module-height);
}

/* Same rule as above, but specifically for the info module / credit button */
.rate-box-with-credit-button .page-rate-widget-box {
	border-radius: 0;
	box-shadow: none;
	background: none;
	border: none;
}

.page-rate-widget-box .rate-points,
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown,
.page-rate-widget-box .cancel {
	display: block;
	border: none;
