/* Article Images */
.scp-image-block {
	border: solid 1px #000;
	box-shadow: 0 1px 6px rgba(0, 0, 0, 0.25);
	width: 300px;
	display: flex;
	flex-flow: column nowrap;
	align-items: center;
	min-width: 100%;
}

.scp-image-block.block-right {
	float: right;
	clear: right;
	margin: 0 0 1em 2em;
}

.scp-image-block.block-left {
	float: left;
	clear: left;
	margin: 0 2em 1em 0;
}

.scp-image-block.block-center {
	margin-right: auto;
	margin-left: auto;
}

.scp-image-block img {
	border: 0;
	min-width: 100%;
}

.scp-image-caption {
	min-width: 100%;
}

.scp-image-block .scp-image-caption {
	background-color: rgba(var(--swatch-menubg-light-color));
	border-top: solid 0.0625rem rgba(var(--swatch-menubg-black-color));
	font-size: 80%;
	font-weight: 700;
	text-align: center;
	padding: 0.125rem 0;
}

.scp-image-block .scp-image-caption > p {
	margin: 0;
	padding: 0 0.625rem;
}

div.block-right,
div.block-left {
	min-width: 30% !important;
}

/* Tables */
table.wiki-content-table * {
	margin: 0px;
	padding: 0px;
	box-sizing: border-box;
}

table.wiki-content-table,
table.wiki-content-table tbody {
	border-collapse: unset;
	border-spacing: 0;
	margin: 0.5em auto;
	display: table;
	background-color: transparent;
}

table.wiki-content-table th {
	background: rgba(var(--swatch-menubg-medium-color));
	color: rgba(var(--swatch-menutxt-light-color));
}

table.wiki-content-table th,
table.wiki-content-table tr {
	border: 0.0625rem solid rgba(var(--swatch-menubg-medium-dark-color), 0.25) !important;
}

.translation-box-stylesheet {
	display: none;
}

/* Mobile Elements */
@media only screen and (max-width: 768px) {
	:root {
		--rating-module-height: 1.5rem;
	}

	div.block-right,
	div.block-left {
		min-width: 40% !important;
		margin: 0 auto;
	}
}

/* most of this is copied from the sidebar */
body#translations {
	background: rgba(var(--swatch-menubg-color));
}

#translations .interwiki__title {
	min-height: 1.5625rem;
	background: var(--gradient-sidemenu-header);
}

#translations .interwiki__title p {
	padding-right: 1rem;
	letter-spacing: 0.2rem;
	color: rgba(var(--swatch-menutxt-light-color));
	height: 1.5625rem;
	line-height: 1.5625rem;
	text-transform: uppercase;
	font-size: calc(var(--base-font-size) * 0.95);
	font-weight: 900;
	text-align: right;
	text-shadow: 0.063rem 0.063rem 0.063rem rgba(var(--swatch-border-color));
	display: block;
}

#translations .interwiki__entry {
	margin: 0;
	text-align: right;
	display: flex;
	flex-direction: row;
	flex-wrap: nowrap;
	align-items: center;
	justify-content: flex-end;
	/*color: transparent;*/
	font-size: 0;
	min-height: calc(2rem - var(--sidebar-internal-border-thickness));
	border-bottom: var(--sidebar-internal-border-thickness) solid rgba(var(--swatch-border-color), 0.08);
	flex-shrink: 2;
}

#translations .interwiki__entry a {
	padding: 0 0.6em;
	margin: 0;
	color: rgba(var(--swatch-menutxt-dark-color));
	font-size: var(--base-font-size);
	letter-spacing: 0.04em;
	background-color: rgba(var(--swatch-menubg-hover-color), 0);
	transition: background-color 80ms cubic-bezier(0.4, 0.0, 0.2, 1);
	display: block;
	height: calc(2rem - var(--sidebar-internal-border-thickness));
	line-height: calc(2rem - var(--sidebar-internal-border-thickness));
	background-clip: padding-box;
}

